    color("DimGray") for (dx=[-1,1], dy=[-1,1])
        translate([-unit*1.25 + dx*unit*0.25, dy*unit*0.25, unit*0.82])
            cube([unit*0.2, unit*0.2, unit*0.15]);

    // Action buttons (right)
    color("Crimson") for (dx=[-1,1], dy=[-1,1])
        translate([unit*1.0 + dx*unit*0.25, dy*unit*0.25, unit*0.82])
            cylinder(h=unit*0.15, r=unit*0.12);
}

module vents() {
    // Rear cooling vents
    color("Gray") for (i=[-4:4])
        translate([i*unit*0.2, -unit*1.45, unit*0.2])
            cube([unit*0.08, unit*0.1, unit*0.1]);
}

module usbc_port() {
