handheld_scanner();

module handheld_scanner() {
    // Main body shell
    difference() {
        // rounded body
        minkowski() {
            cube([80, 40, 25], center = true);
            sphere(corner_r);
        }
        translate([0, 0, 12.5 + corner_r])
            cube([76, 36, 30], center = true);   // hollow
    }

    // grip area pattern (knurling simulation)
    translate([0, 0, -12])
        grip_texture();

    // top handle extension
    translate([0, -20, 10])
        rotate([90, 0, 0])
            cylinder(h = 30, d1 = 22, d2 = 18, $fn = 36);

    // scan window
    translate([-35, 0, 0])
        rotate([0, 90, 0])
            cylinder(h = 40, d = 16, $fn = 60);

    // LCD screen
    color("black")
    translate([10, 0, 12.5])
        cube([28, 18, scr_th], center = true);

    // Buttons
    color("dimgray")
    for (y = [-8, 0, 8])
        translate([25, y, 12.5 + btn_h/2])
            cylinder(h = btn_h, r = btn_r, $fn = 20);

    // Side trigger button
    color("firebrick")
    translate([0, -20, -5])
        rotate([0, 90, 0])
            cylinder(h = 15, r1 = 8, r2 = 5, $fn = 32);

    // micro-USB port
    color("black")
    translate([0, 20, 0])
        cube([usb_w, wall, usb_h], center = true);

    // Charging LED indicator
    color("lime")
    translate([0, 20, 3])
        sphere(d = led_d);
}

// ---- Grip texture using small cubes --------------------------------------
module grip_texture() {
    step = 3;
    for (x = [-30:step:30])
        for (y = [-15:step:15])
            if ((x + y) % (2*step) == 0)
                translate([x, y, -12.5])
                    cube([step-0.5, step-0.5, 1], center = true);
}

