// Arena Electronic Scoreboard
// Compact 50mm wide arena-style electronic scoreboard with LED digits and control buttons

arena_size = 50;
wall_thickness = 2;
screen_depth = 3;

module arena_base() {
    difference() {
        // Main arena base
        cube([arena_size, arena_size*0.7, wall_thickness*2], center=true);

        // Inner recessed area
        translate([0, 0, wall_thickness])
            cube([arena_size-wall_thickness*2, arena_size*0.7-wall_thickness*2, wall_thickness], center=true);
    }
}

module led_screen() {
    color([0.1, 0.1, 0.1])  // Dark screen surface
    difference() {
        // Main screen
        translate([0, 0, wall_thickness+screen_depth/2])
            cube([arena_size*0.8, arena_size*0.4, screen_depth], center=true);

        // LED segments for digit display
        for(i=[-1, 1]) {
            translate([i*8, 0, wall_thickness+screen_depth+0.1])
                color([0.8, 0, 0])  // Red LED color
                cube([3, 12, 0.2], center=true);

            translate([i*8, -5, wall_thickness+screen_depth+0.1])
                color([0.8, 0, 0])
                cube([8, 3, 0.2], center=true);

            translate([i*8, 5, wall_thickness+screen_depth+0.1])
                color([0.8, 0, 0])
                cube([8, 3, 0.2], center=true);
        }
    }
}

module control_buttons() {
    // Power button
    translate([arena_size/2-8, arena_size*0.3/2+3, wall_thickness+1])
        color([0.2, 0.2, 0.2])
        cylinder(h=2, r=3, center=true);

    // Mode button
    translate([arena_size/2-8, 0, wall_thickness+1])
        color([0.2, 0.2, 0.2])
        cylinder(h=2, r=2.5, center=true);

    // Reset button
    translate([arena_size/2-8, -arena_size*0.3/2-3, wall_thickness+1])
        color([0.2, 0.2, 0.2])
        cylinder(h=2, r=2.5, center=true);
}

module usb_port() {
    color([0.3, 0.3, 0.3])
    translate([-arena_size/2+1, 0, wall_thickness])
        cube([2, 8, 4], center=true);
}

module status_leds() {
    // Power indicator
    translate([0, arena_size*0.4/2+2, wall_thickness+screen_depth+0.1])
        color([0, 0.8, 0])
        sphere(r=1, center=true);

    // Status indicator
    translate([5, arena_size*0.4/2+2, wall_thickness+screen_depth+0.1])
        color([0, 0, 0.8])
        sphere(r=1, center=true);
}

// Assembly
union() {
    color([0.9, 0.9, 0.9]) arena_base();
    led_screen();
    control_buttons();
    usb_port();
    status_leds();

    // Arena walls
    color([0.85, 0.85, 0.85])
    for(side=[-1, 1]) {
        translate([0, side*arena_size*0.35, wall_thickness*1.5])
            cube([arena_size+2, wall_thickness, wall_thickness*3], center=true);
    }

    for(side=[-1, 1]) {
        translate([side*arena_size/2+1, 0, wall_thickness*1.5])
            rotate([0, 0, 90])
            cube([arena_size*0.7+2, wall_thickness, wall_thickness*3], center=true);
    }
}

// Stage Electronic Device - Wireless In-Ear Monitor System Receiver
// Realistic style, medium complexity, 3D printable

$fn = 64;

// Main body dimensions
body_w = 45;
body_h = 65;
body_d = 18;

// Colors
color_body = [0.2, 0.2, 0.2];
color_screen = [0.1, 0.1, 0.1];
color_buttons = [0.4, 0.4, 0.4];
color_antenna = [0.3, 0.3, 0.3];
color_led = [0, 1, 0];
color_text = [1, 1, 1];

// Main body
module main_body() {
    difference() {
        color(color_body)
        rounded_cube([body_w, body_h, body_d], 3);

        // Screen recess
        translate([body_w/2 - 18, body_h - 12, -0.1])
            cube([36, 20, body_d + 0.2]);

        // Button holes
        for(y = [body_h - 30, body_h - 40, body_h - 50]) {
            translate([body_w/2 + 15, y, -0.1])
                cylinder(h = body_d + 0.2, r = 3);
        }

        // Clip groove
        translate([body_w - 3, body_h/2, body_d/2])
            rotate([0, 90, 0])
                cylinder(h = 30, r = 1.5);
    }
}

// Helper for rounded cube
module rounded_cube(size, radius) {
    hull() {
        for(dx = [radius, size.x - radius])
            for(dy = [radius, size.y - radius])
                for(dz = [radius, size.z - radius])
                    translate([dx, dy, dz])
                        sphere(r = radius);
    }
}

// OLED screen
module screen() {
    translate([body_w/2, body_h - 12, body_d/2 + 0.1])
        color(color_screen)
        cube([32, 16, 0.5], center = true);
}

// Channel display
module channel_display() {
    translate([body_w/2, body_h - 25, body_d/2 + 0.1])
        color(color_text)
        cube([6, 8, 0.3], center = true);
}

// Buttons
module buttons() {
    for(y = [body_h - 30, body_h - 40, body_h - 50]) {
        translate([body_w/2 + 15, y, body_d/2])
            color(color_buttons)
            cylinder(h = 2, r = 2.5, center = true);
    }
}

// LED indicators
module leds() {
    translate([body_w/2 - 15, body_h - 5, body_d/2 + 0.1])
        color(color_led)
        sphere(r = 1.5);

    translate([body_w/2 + 15, body_h - 5, body_d/2 + 0.1])
        color([1, 0, 0])
        sphere(r = 1.5);
}

// Volume knob
module volume_knob() {
    translate([5, body_h/2, body_d/2])
        rotate([90, 0, 0])
            color(color_body)
            difference() {
                cylinder(h = 4, r = 6);
                cylinder(h = 5, r = 3);
            }
}

// Antenna
module antenna() {
    translate([0, body_h, body_d/2])
        rotate([0, -30, 0])
            union() {
                color(color_antenna)
                cylinder(h = 25, r = 1.5);
                translate([0, 0, 25])
                    sphere(r = 2);
            }
}

// Belt clip
module belt_clip() {
    translate([body_w - 2, body_h/2, body_d/2])
        rotate([0, 90, 0])
            difference() {
                color(color_body)
                union() {
                    cube([30, 6, 2], center = true);
                    translate([15, 0, 0])
                        cube([2, 6, 8], center = true);
                }
                translate([5, 0, 0])
                    cube([20, 8, 5], center = true);
            }
}

// Audio jack
module audio_jack() {
    translate([body_w/2, 8, body_d/2])
        rotate([90, 0, 0])
            difference() {
                color(color_body)
                cylinder(h = 8, r = 3.5);
                cylinder(h = 9, r = 2.5);
            }
}

// USB-C port
module usb_port() {
    translate([body_w - 8, 8, body_d/2])
        rotate([90, 0, 0])
            color(color_body)
            cube([8, 4, 3], center = true);
}

// Assembly
main_body();
screen();
channel_display();
buttons();
leds();
volume_knob();
antenna();
belt_clip();
audio_jack();
usb_port();

