color_ports = [0.3, 0.3, 0.3];

// Main phone body
module phone_body() {
    color(color_phone)
    difference() {
        // Rounded body
        minkowski() {
            cube([phone_width - 2*corner_radius, phone_height - 2*corner_radius, phone_depth], center = true);
            cylinder(r = corner_radius, h = phone_depth, center = true);
        }

        // Screen cutout
        translate([0, 0, phone_depth/2 - 0.1])
            cube([phone_width - 2*screen_bezel, phone_height - 2*screen_bezel - 20, phone_depth + 0.2], center = true);
    }
}

// Screen module
module phone_screen() {
    color(color_screen)
    translate([0, 10, phone_depth/2 - 0.2])
        cube([phone_width - 2*screen_bezel - 2, phone_height - 2*screen_bezel - 22, 0.5], center = true);
}

// Camera bump
module camera_bump() {
    color(color_phone)
    translate([phone_width/2 - 15, phone_height/2 - 15, phone_depth/2 + 1])
        cylinder(r = 12, h = 2, $fn = 32);

    // Camera lenses
    color(color_camera)
    for(i = [0:1]) {
        translate([phone_width/2 - 19 + i*8, phone_height/2 - 15, phone_depth/2 + 3.1])
            cylinder(r = 3, h = 0.5, $fn = 16);
    }

    // Flash
    translate([phone_width/2 - 11, phone_height/2 - 15, phone_depth/2 + 3.1])
        cylinder(r = 2, h = 0.5, $fn = 16);
