    color(color_button)
    // Power button
    translate([phone_width/2 + 0.1, 30, 0])
        cube([1, 15, 3], center = true);

    // Volume buttons
    translate([-phone_width/2 - 0.1, 20, 0])
        cube([1, 20, 3], center = true);
    translate([-phone_width/2 - 0.1, -10, 0])
        cube([1, 20, 3], center = true);
}

// Speaker grill
module speaker_grill() {
    color(color_phone)
    translate([0, -phone_height/2 + 10, phone_depth/2 - 0.5])
        cube([60, 6, 1], center = true);

    // Holes
    color([0, 0, 0])
    for(i = [-5:5]) {
        translate([i*5, -phone_height/2 + 10, phone_depth/2])
            cylinder(r = 1, h = 2, $fn = 8);
    }
