    translate([bot_length/2-15, -bot_width/4, bot_height-wall_thickness])
    color(color_screen)
    cube([20, 15, 0.5], center=false);

    // Screen bezel
    translate([bot_length/2-15, -bot_width/4, bot_height-0.5])
    color([0.5,0.5,0.5])
    cube([20, 15, 0.5], center=false);
}

// Control buttons
module buttons() {
    for(i=[0:2]) {
        translate([bot_length/2-30, -bot_width/2+10+i*8, bot_height-1])
        color([0.1,0.1,0.1])
        cylinder(h=2, r=3);
    }
}

// LED status lights
module status_lights() {
    // Front LED
    translate([bot_length/2-2, 0, bot_height/2])
    color(color_light)
    sphere(r=2);

    // Rear LED
    translate([-bot_length/2+2, 0, bot_height/2])
    color(color_light)
    sphere(r=2);
}

// Antenna assembly
module antenna() {
    translate([0, -bot_width/4, bot_height+2])
    rotate([0,10,0])
    union() {
        // Base
        color(color_body)
        cylinder(h=3, r=4);

        // Mast
        translate([0,0,3])
        color(color_antenna)
        cylinder(h=15, r=1);

        // Top
        translate([0,0,18])
        color(color_antenna)
        sphere(r=2);
    }
}

// Sensor array
module sensors() {
    // Ultrasonic sensor
    translate([bot_length/2-5, 0, bot_height/2])
    union() {
        color([0.3,0.3,0.3])
        cube([8, 12, 6], center=true);

        // Sensor eyes
        translate([3, -3, 3.5])
        color([0.1,0.1,0.1])
        cylinder(h=1, r=1.5);

        translate([3, 3, 3.5])
        color([0.1,0.1,0.1])
        cylinder(h=1, r=1.5);
    }

    // IR sensor
    translate([bot_length/2-5, 0, bot_height/2-8])
    color([0.1,0.1,0.1])
    cube([4, 4, 2], center=true);
}

// Wheels with tread pattern
module wheels() {
    module wheel() {
        rotate([90,0,0])
        difference() {
            // Main wheel
            color(color_wheel)
            cylinder(h=8, r=12);

            // Hollow center
            cylinder(h=9, r=4);

            // Tread pattern
            for(i=[0:7]) {
                rotate([0,0,i*45])
                translate([0,12.5,4])
                cube([2, 1, 8], center=true);
            }
        }
    }

    // Left wheels
    translate([-bot_length/4, -bot_width/2-6, 6])
    wheel();
    translate([bot_length/4, -bot_width/2-6, 6])
    wheel();

    // Right wheels
    translate([-bot_length/4, bot_width/2+6, 6])
    wheel();
    translate([bot_length/4, bot_width/2+6, 6])
    wheel();
}

// Charging port
module charging_port() {
    translate([-bot_length/2+2, bot_width/4, bot_height/2-5])
    difference() {
        color([0.2,0.2,0.2])
        cube([4, 6, 3], center=true);

        // Port hole
        translate([0,0,-1])
        cylinder(h=5, r=1.5);
    }
}

// Assembly
body_shell();
screen();
buttons();
status_lights();
antenna();
sensors();
wheels();
charging_port();

