    translate([0, body_height/4, body_depth/2-screen_depth/2])
        cube([screen_width, screen_height, screen_depth], center=true);

    // Screen bezel
    color(color_accent)
    translate([0, body_height/4, body_depth/2-screen_depth/2-0.5])
        cube([screen_width+4, screen_height+4, 0.5], center=true);
