// Button array
module buttons() {
    // Navigation buttons (D-pad style)
    color(color_buttons)
    for(i = [-1, 0, 1]) {
        for(j = [-1, 0, 1]) {
            if(!(i == 0 && j == 0)) {
                translate([i*button_spacing, -body_height/4 + j*button_spacing, body_depth/2-button_height/2])
                    cylinder(h=button_height, d=button_diameter, center=true);
            }
        }
    }

    // Center action button
    translate([0, -body_height/4, body_depth/2-button_height/2])
        cylinder(h=button_height, d=button_diameter*1.2, center=true);

    // Side buttons
    color(color_buttons)
    translate([body_width/2+button_height/2, 0, 0])
        rotate([0, 90, 0])
            cylinder(h=button_height, d=button_diameter, center=true);

    translate([-body_width/2-button_height/2, 0, 0])
        rotate([0, 90, 0])
            cylinder(h=button_height, d=button_diameter, center=true);
}

