    color(color_body)
    difference() {
        // Cover
        translate([0, body_height/6, -body_depth/2+battery_depth/2+wall_thickness])
            cube([battery_width, battery_height, battery_depth], center=true);

        // Screw holes
        for(pos = [[-battery_width/2+5, -battery_height/2+5], 
                   [battery_width/2-5, -battery_height/2+5],
                   [battery_width/2-5, battery_height/2-5],
                   [-battery_width/2+5, battery_height/2-5]]) {
            translate([pos[0], body_height/6+pos[1], -body_depth/2+battery_depth])
                cylinder(h=battery_depth+1, d=2, center=true);
        }
    }

    // Hinge
    color(color_body)
    translate([battery_width/2-2, body_height/6, -body_depth/2+wall_thickness])
        rotate([90, 0, 0])
            cylinder(h=battery_height-4, d=2, center=true);
}

