// --- Helper modules -----------------------------------------------------
module rounded_box(size, r) {
    hull() {
        for(dx=[-1,1], dy=[-1,1], dz=[-1,1])
            translate([dx*(size.x/2-r), dy*(size.y/2-r), dz*(size.z/2-r)])
                sphere(r=r, $fn=16);
