color_case = [0.3, 0.3, 0.3];
color_buttons = [0.2, 0.2, 0.2];
color_accents = [0.8, 0.8, 0.8];
color_port = [0.1, 0.1, 0.1];

// Main dimensions
device_width = 80;
device_height = 120;
device_thickness = 15;
screen_margin = 5;
wall_thickness = 2;

// Main body
module main_body() {
    difference() {
        // Outer shell
        color(color_case)
        rounded_cube([device_width, device_height, device_thickness], 5);

        // Screen cutout
        translate([0, 0, -0.1])
        color(color_screen)
        rounded_cube([device_width - screen_margin*2, 
                     device_height - screen_margin*2, 
                     device_thickness + 0.2], 3);
    }
}

// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        translate([radius, radius, 0]) 
            cube([size[0] - radius*2, size[1] - radius*2, size[2]]);
        translate([radius, radius, radius]) 
            sphere(r = radius);
        translate([size[0] - radius, radius, radius]) 
            sphere(r = radius);
        translate([radius, size[1] - radius, radius]) 
            sphere(r = radius);
        translate([size[0] - radius, size[1] - radius, radius]) 
            sphere(r = radius);
    }
}

// Screen
module screen() {
    translate([0, 0, device_thickness - 0.5])
    color(color_screen)
    difference() {
        cube([device_width - screen_margin*2 - 2, 
              device_height - screen_margin*2 - 2, 
              1], center = true);
        // Map display area
        translate([0, 0, -0.2])
        cube([device_width - screen_margin*2 - 6, 
              device_height - screen_margin*2 - 6, 
              1.5], center = true);
    }
}

// Map details on screen
module map_display() {
    translate([0, 0, device_thickness - 0.2])
    color([0.1, 0.8, 0.2])
    for(i = [-2:2]) {
        for(j = [-3:3]) {
            if((i+j) % 2 == 0) {
                translate([i*8, j*8, 0])
                cube([6, 6, 0.1], center = true);
            }
        }
    }

    // Roads
    color([0.9, 0.9, 0.9])
    translate([0, 0, 0.1])
    cube([device_width - 20, 2, 0.1], center = true);
    translate([0, 0, 0.1])
    rotate([0, 0, 90])
    cube([device_height - 30, 2, 0.1], center = true);

    // Current location marker
    color([1, 0, 0])
    translate([0, -10, 0.2])
    cylinder(h = 0.5, r = 3, $fn = 16);
}

// Buttons
module buttons() {
    // Power button
    translate([device_width/2 - 5, device_height/2 + 5, device_thickness/2])
    rotate([90, 0, 0])
    color(color_buttons)
    cylinder(h = 3, r = 2, $fn = 16);

    // Menu buttons
    for(i = [0:2]) {
        translate([-device_width/2 + 5 + i*8, device_height/2 - 10, device_thickness/2])
        rotate([90, 0, 0])
        color(color_buttons)
        rounded_cube([6, 6, 2], 1);
    }
}

// USB port
module usb_port() {
    translate([0, -device_height/2, 0])
    color(color_port)
    difference() {
        cube([8, 4, 3], center = true);
        translate([0, 0, 1])
        cube([6, 3, 3], center = true);
    }
}

// Speaker grill
module speaker() {
    translate([device_width/2 - 10, 0, device_thickness/2])
    rotate([90, 0, 0])
    color(color_port)
    for(i = [0:4]) {
        translate([0, i*2 - 4, 0])
        cube([0.5, 1, 8], center = true);
    }
}

// Antenna bump
module antenna() {
    translate([0, device_height/2 - 10, device_thickness/2 + 2])
    color(color_case)
    difference() {
        cylinder(h = 4, r = 8, $fn = 32);
        translate([0, 0, -0.1])
        cylinder(h = 4.2, r = 6, $fn = 32);
    }
}

// Mounting clip
module mounting_clip() {
    translate([0, -device_height/2 - 5, -device_thickness/2])
    color(color_case)
    difference() {
        union() {
            cube([device_width - 40, 10, 5], center = true);
            translate([0, -5, 0])
            cube([device_width - 50, 10, 5], center = true);
        }
        translate([0, -2.5, -2.5])
        cube([device_width - 45, 5, 10], center = true);
    }
}

