    difference() {
        // Outer shell
        color(color_case)
        cube([device_length, device_width, device_height], center = true);

        // Hollow interior
        translate([0, 0, -wall_thickness/2])
        cube([device_length - wall_thickness*2, device_width - wall_thickness*2, device_height - wall_thickness], center = true);

        // Front drive bays opening
        translate([0, 0, -device_height/2 + wall_thickness/2])
        cube([device_length - wall_thickness*2, device_width - wall_thickness*2, wall_thickness], center = true);
    }
}

// Individual drive bay module
module drive_bay() {
    difference() {
        // Drive tray
        color(color_drives)
        cube([drive_bay_width, device_width - wall_thickness*2 - 4, drive_bay_width - 2], center = true);

        // Drive handle opening
        translate([0, -device_width/2 + wall_thickness + 2, 0])
        cube([drive_bay_width - 4, 4, drive_bay_width - 2], center = true);

        // Ventilation holes
        for(i = [-1:1]) {
            translate([0, i*3, 0])
            rotate([90, 0, 0])
            cylinder(h = drive_bay_width - 2, d = 1.5, center = true);
