color([0.15, 0.15, 0.15]) {
    // Main body shell
    difference() {
        // Outer shell
        translate([-50, -35, 0])
            cube([100, 70, 30], center=true);

        // Inner cavity
        translate([-48, -33, -1])
            cube([96, 66, 32], center=true);
    }
