// Control buttons panel
translate([-45, -25, 30]) {
    color([0.8, 0.8, 0.8]) {
        cube([25, 50, 2]);
    }

    // Play button (triangle)
    color([0.2, 0.2, 0.2]) {
        translate([5, 15, 2.5]) {
            rotate([0, 0, 90])
                cylinder(h=0.5, r1=3, r2=0);
        }
    }

    // Stop button (square)
    color([0.2, 0.2, 0.2]) {
        translate([5, 5, 2.5])
            cube([4, 4, 0.5], center=true);
    }

    // Rewind button (double triangle)
    color([0.2, 0.2, 0.2]) {
        translate([5, 0, 2.5]) {
            for(i=[-1, 1]) {
