            }
        }
    }

    // Fast forward button
    color([0.2, 0.2, 0.2]) {
        translate([5, -10, 2.5]) {
            for(i=[-1, 1]) {
                translate([i*1.5, 0, 0]) {
                    rotate([0, 0, 90])
                        cylinder(h=0.5, r1=2, r2=0);
