// Legacy Game Boy DMG-01
// Overall dimensions: 90 x 148 x 32 mm

// Colors
screen_color = [0.1, 0.1, 0.1];
case_color = [0.9, 0.9, 0.8];
button_a_color = [0.9, 0.1, 0.2];
button_b_color = [0.9, 0.1, 0.2];
dpad_color = [0.1, 0.1, 0.1];
screen_bezel_color = [0.7, 0.7, 0.7];

// Main shell
module main_shell() {
    difference() {
        // Outer case
        color(case_color)
        minkowski() {
            cube([90, 148, 25], center = true);
            sphere(2);
        }

        // Hollow interior
        translate([0, 0, -2])
        scale([0.9, 0.9, 0.8])
        cube([90, 148, 30], center = true);

        // Screen cutout
        translate([0, 50, 12])
        cube([65, 45, 5], center = true);

        // Button holes
        translate([25, -20, 12])
        cylinder(h = 5, r = 8, center = true);

        translate([25, -45, 12])
        cylinder(h = 5, r = 8, center = true);

        // D-pad hole
        translate([-25, -32, 12])
        cube([30, 30, 5], center = true);

        // Speaker grill holes
        for (i = [-3:3]) {
            for (j = [-1:1]) {
                translate([0 + j * 5, -90 + i * 5, 12])
                cylinder(h = 5, r = 1, center = true);
            }
        }
    }
}

// Screen bezel
module screen_bezel() {
    color(screen_bezel_color)
    translate([0, 50, 12])
    cube([70, 50, 2], center = true);

    // Actual screen
    color(screen_color)
    translate([0, 50, 13])
    cube([60, 40, 1], center = true);
}

// D-pad
module dpad() {
    color(dpad_color)
    translate([-25, -32, 6])
    cube([28, 28, 3], center = true);

    // Up
    translate([-25, -42, 6])
    cube([8, 10, 3], center = true);

    // Down
    translate([-25, -22, 6])
    cube([8, 10, 3], center = true);

    // Left
    translate([-35, -32, 6])
    cube([10, 8, 3], center = true);

    // Right
    translate([-15, -32, 6])
    cube([10, 8, 3], center = true);
}

// Buttons
module buttons() {
    // A button
    color(button_a_color)
    translate([25, -20, 6])
    cylinder(h = 5, r = 7, center = true);

    // B button
    color(button_b_color)
    translate([25, -45, 6])
    cylinder(h = 5, r = 7, center = true);
}

// Select/Start buttons
module select_start() {
    color(dpad_color)
    for (i = [-1:1]) {
        translate([0 + i * 12, -65, 6])
        cube([8, 3, 2], center = true);
    }
}

// Power switch
module power_switch() {
    color(case_color)
    translate([-35, 70, 6])
    cube([10, 5, 2], center = true);

    color(dpad_color)
    translate([-30, 70, 6])
    cube([5, 2, 1], center = true);
}

// Cartridge slot
module cartridge_slot() {
    color([0.2, 0.2, 0.2])
    translate([0, 90, 10])
    cube([60, 15, 10], center = true);
}

// Link port
module link_port() {
    color([0.2, 0.2, 0.2])
    translate([40, 70, 0])
    cube([10, 15, 8], center = true);
}

// Assemble the Game Boy
main_shell();
screen_bezel();
dpad();
buttons();
select_start();
power_switch();
cartridge_slot();
link_port();

// Vintage Electronic Pocket Calculator (Casio style, ~1975)
$fn = 32;

// Main body shell
module body() {
    difference() {
        // Outer rounded shell
        minkowski() {
            cube([75, 120, 15], center=true);
            sphere(3);
        }
        // Hollow interior
        translate([0,0,-1])
            cube([71, 116, 20], center=true);
    }
}

// Display window with realistic green LCD
module display() {
    translate([0, 35, 8]) {
        // Display bezel
        color([0.2,0.2,0.2])
            cube([55, 25, 3], center=true);

        // LCD screen
        translate([0,0,2])
            color([0.1,0.3,0.1])
            cube([50, 20, 1], center=true);

        // Reflection effect
        translate([-20,-8,3])
            scale([1,0.3,1])
            color([0.8,0.9,0.8,0.5])
            sphere(3);
    }
}

// Individual calculator button
module calc_button(label, x, y) {
    translate([x, y, 8]) {
        // Button base
        color([0.1,0.1,0.1])
            cylinder(h=3, r=5);

        // Button top
        translate([0,0,3])
            color([0.7,0.7,0.7])
            cylinder(h=2, r=4.5);

        // Button label (raised)
        translate([0,0,5.2])
            scale([0.1,0.1,1])
            color([0,0,0])
            text(label, size=12, h=0.5, center=true);
    }
}

// Button grid layout
module keypad() {
    // Number buttons 0-9
    for (i = [0:9]) {
        x = ((i-1) % 3 - 1) * 12;
        y = (floor((i-1)/3) - 2) * 12 - 10;
        if (i > 0) calc_button(str(i), x, y);
    }

    // Zero button
    calc_button("0", -12, -46);

    // Operation buttons
    calc_button("+", 12, -10);
    calc_button("-", 12, -22);
    calc_button("*", 12, -34);
    calc_button("/", 12, -46);
    calc_button("=", 24, -46);
    calc_button(".", 0, -46);
    calc_button("C", 24, -10);
}

// Battery compartment cover
module battery_cover() {
    translate([0, -45, -6.5]) {
        color([0.3,0.3,0.3])
            cube([30, 20, 1], center=true);

        // Screw
        translate([10,-5,-1])
            rotate([180,0,0])
            cylinder(h=3, r=2);

        // Grooves for grip
        for (i = [-2:2]) {
            translate([i*4, 0, 0.6])
                color([0.1,0.1,0.1])
                cube([1, 18, 0.5], center=true);
        }
    }
}

// Model logo
module logo() {
    translate([0, 55, 8.5])
        rotate([0,0,0])
        scale([0.1,0.1,1])
        color([0.1,0.1,0.1])
        text("CASIO", size=20, h=0.5, center=true);
}

// Assembly
union() {
    // Main body in beige plastic
    color([0.85,0.8,0.7])
        body();

    // Display assembly
    display();

    // Keypad
    keypad();

    // Battery cover
    battery_cover();

    // Brand logo
    logo();

    // Side port (mini USB style)
    translate([38, 0, 0])
        rotate([0,90,0])
        color([0.2,0.2,0.2])
        cylinder(h=8, r=3);
}

// Pixel Electronic Device - Realistic Medium Complexity
// A retro-inspired handheld gaming console with pixel art styling

// Device parameters
$fn = 64;
device_length = 85;
device_width = 50;
device_height = 15;

// Colors
body_color = "#2E3440";
screen_color = "#3B4252";
bezel_color = "#4C566A";
button_color = "#D8DEE9";
dpad_color = "#ECEFF4";
port_color = "#2E3440";

// Main body module
module device_body() {
    difference() {
        // Main shell
        color(body_color)
        rounded_cube([device_length, device_width, device_height], 3);

        // Top face chamfer
        translate([0, 0, device_height - 1.5])
        scale([1, 1, 0.5])
        rounded_cube([device_length + 0.5, device_width + 0.5, 3], 3);
    }
}

// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        for(x = [-1, 1])
        for(y = [-1, 1])
        for(z = [-1, 1])
        translate([x * (size[0]/2 - radius), y * (size[1]/2 - radius), z * (size[2]/2 - radius)])
        sphere(r = radius);
    }
}

// Screen assembly
module screen() {
    // Screen bezel
    color(bezel_color)
    translate([0, 12, device_height - 0.5])
    cube([device_length - 10, 30, 1.5], center = true);

    // Actual screen
    color(screen_color)
    translate([0, 12, device_height])
    cube([device_length - 14, 26, 0.5], center = true);

    // Pixel grid pattern
    color("#88C0D0")
    for(i = [-6:6])
    for(j = [-3:3])
    translate([i * 3, 12 + j * 3, device_height + 0.3])
    cube([1.5, 1.5, 0.1], center = true);
}

// D-pad module
module dpad() {
    color(dpad_color)
    translate([-device_length/2 + 15, -15, 4])
    union() {
        // Center
        cube([8, 8, 2], center = true);

        // Four directions
        translate([0, -7, 0]) cube([8, 6, 2], center = true);
        translate([0, 7, 0]) cube([8, 6, 2], center = true);
        translate([-7, 0, 0]) cube([6, 8, 2], center = true);
        translate([7, 0, 0]) cube([6, 8, 2], center = true);
    }
}

// Action buttons
module action_buttons() {
    // Button positions
    positions = [[device_length/2 - 18, -10], [device_length/2 - 12, -16]];

    for(pos = positions) {
        color(button_color)
        translate([pos[0], pos[1], 4])
        cylinder(h = 2.5, r = 3.5, $fn = 32);

        // Button symbols
        color(port_color)
        translate([pos[0], pos[1], 6.6])
        rotate([0, 180, 0])
        linear_extrude(0.5)
        text("A", size = 2.5, halign = "center", valign = "center");
    }
}

// Start/Select buttons
module utility_buttons() {
    for(i = [0, 1]) {
        color(button_color)
        translate([5 + i * 12, 0, 4])
        rounded_cube([10, 4, 1.5], 1);
    }
}

// Speaker grille
module speaker() {
    color(bezel_color)
    difference() {
        translate([0, -device_width/2 + 5, 2])
        rounded_cube([device_length - 20, 6, 1], 1);

        // Speaker holes
        for(i = [-5:5])
        translate([-i * 6, 0, 0.5])
        cylinder(h = 2, r = 0.8, $fn = 16);
    }
}

// USB-C port
module usb_port() {
    color(port_color)
    translate([device_length/2 - 5, 0, 3])
    cube([8, 4, 2], center = true);

    // Port opening
    color("#000000")
    translate([device_length/2 + 0.1, 0, 3])
    cube([1, 3, 1.5], center = true);
}

// Power LED indicator
module power_led() {
    color("#BF616A")
    translate([-device_length/2 + 5, device_width/2 - 3, 4])
    cylinder(h = 0.5, r = 1.5, $fn = 16);
}

// Main assembly
device_body();
screen();
dpad();
action_buttons();
utility_buttons();
speaker();
usb_port();
power_led();

