// Voxel Smartphone - realistic medium-complexity
// Designed for 3D printing, 80 mm tall

$fn = 32;

// Main colors
color_screen = [0.05, 0.05, 0.05];
color_body = [0.15, 0.15, 0.15];
color_frame = [0.25, 0.25, 0.25];
color_button = [0.5, 0.5, 0.5];
color_cam = [0.1, 0.1, 0.1];

// Dimensions
phone_w = 40;
phone_h = 80;
phone_d = 8;
voxel = 2;

module voxel_cube(x,y,z, col) {
    color(col)
    translate([x*voxel, y*voxel, z*voxel])
    cube([voxel, voxel, voxel], center=true);
}

// Main body frame
module phone_body() {
    color(color_frame)
    difference() {
        // Outer shell
        cube([phone_w, phone_h, phone_d], center=true);
        // Hollow inside
        translate([0,0,-1])
        cube([phone_w-4, phone_h-4, phone_d-2], center=true);
    }
}

// Screen array
module screen() {
    for (y = [-19:2:19]) {
        for (x = [-18:2:18]) {
            voxel_cube(x, y, 4, color_screen);
        }
    }
}

// Side buttons
module buttons() {
    color(color_button)
    // Power button
    translate([phone_w/2+0.1, 15, 0])
    cube([2, 6, 2], center=true);

    // Volume buttons
    translate([phone_w/2+0.1, -5, 0])
    cube([2, 8, 2], center=true);

    translate([phone_w/2+0.1, -15, 0])
    cube([2, 8, 2], center=true);
}

// Camera bump
module camera_bump() {
    translate([0, 35, phone_d/2+1])
    color(color_body)
    cube([16, 16, 2], center=true);

    // Camera lens
    translate([-5, 38, phone_d/2+2.5])
    color(color_cam)
    cylinder(h=3, r=3, center=true);

    // Flash
    translate([5, 38, phone_d/2+2.5])
    color([1,1,0.8])
    cylinder(h=2, r=2, center=true);

    // Second camera
    translate([-5, 32, phone_d/2+2.5])
