camera_depth = 3;

// Speaker grill holes
hole_diameter = 1.5;
hole_spacing = 3;

// Main body module
module texel_body() {
    difference() {
        // Main body shell
        color("silver")
        translate([0, 0, device_thickness/2])
        rounded_cube([device_width, device_height, device_thickness], 4);

        // Screen cutout
        translate([0, 0, device_thickness - 0.5])
        rounded_cube([screen_width + screen_bezel*2, screen_height + screen_bezel*2, device_thickness], 2);

        // USB-C port
        translate([0, -device_height/2 + 5, device_thickness/2])
        cube([usb_width, usb_depth, usb_height], center=true);

        // Headphone jack
        translate([-device_width/2 + 10, -device_height/2 + 5, device_thickness/2])
        cylinder(h=usb_depth*1.5, d=6, center=true);
    }
}

// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        for(x = [-1, 1]) {
            for(y = [-1, 1]) {
                for(z = [-1, 1]) {
                    translate([x*(size[0]/2 - radius), y*(size[1]/2 - radius), z*(size[2]/2 - radius)])
                    sphere(r=radius);
                }
            }
        }
    }
}

// Screen module
module texel_screen() {
    // Screen bezel
    color("darkslategray")
    translate([0, 0, device_thickness - 1])
    rounded_cube([screen_width + screen_bezel*2, screen_height + screen_bezel*2, screen_thickness], 2);

    // Actual screen
    color("black")
    translate([0, 0, device_thickness + 0.5])
    cube([screen_width, screen_height, 0.5], center=true);

    // Screen protector glass
