// OpenSCAD code for a realistic-styled wireless earbud charging case (medium complexity)
// Total footprint ~55 mm Ã— 45 mm Ã— 25 mm â€“ well within typical FDM printer beds

// --- CONFIGURATION -----------------------------------------------------------
case_color       = [0.95,0.95,0.95];    // matte white plastic
lid_color        = [0.92,0.92,0.92];    // slightly off-white
button_color     = [0.15,0.15,0.15];    // dark grey silicone
led_color        = [0.0,1.0,0.0];       // green power indicator
metal_color      = [0.65,0.65,0.65];    // aluminium USB-C bezel

wall_thickness   = 1.2;                 // safe for FDM printing
hinge_pin_r      = 1.0;
button_depth     = 0.8;                 // shallow push-button
led_diam         = 2.0;
usb_c_depth      = 3.5;

// --- MAIN ASSEMBLY ----------------------------------------------------------
module charging_case() {
    difference() {
        union() {
            // bottom shell
            color(case_color)
                rounded_box([55,45,12], wall_thickness);

            // top shell (lid)
            translate([0,0,12])
                rotate([180,0,0])  // flip so the lid opens upward
                color(lid_color)
                    rounded_box([55,45,12], wall_thickness);

            // hinge pins
            for(x=[-20,20])
                translate([x,0,12])
                    color(metal_color)
                        cylinder(h=wall_thickness*2, r=hinge_pin_r, $fn=16);
        }

        // cavity for earbuds
        mirror([0,0,1]) translate([0,0,-12+wall_thickness])
            rounded_box([45,35,9.5], wall_thickness);

        // button cutout
        translate([27,-22-wall_thickness/2,6])
            cube([8,wall_thickness+0.2,6], center=true);

        // LED indicator hole
        translate([-24,-22-wall_thickness/2,8])
            rotate([90,0,0])
                cylinder(h=wall_thickness+0.2, d=led_diam, $fn=16);

        // USB-C port hole
        translate([-27.5-wall_thickness/2,0,3.5])
            rotate([0,90,0])
                cylinder(h=usb_c_depth+0.2, d=8.4, $fn=32);
    }

    // physical detail modules
    pairing_button();
    status_led();
    usb_c_port();
}

// --- HELPER MODULES ----------------------------------------------------------
module rounded_box(size, wt) {
    // creates a hollow rounded box with wall thickness wt
    offset = wt/2;
    hull() {
        for(x=[offset,size.x-offset])
            for(y=[offset,size.y-offset])
                translate([x,y,offset])
                    sphere(r=wt, $fn=16);
        for(x=[offset,size.x-offset])
            for(y=[offset,size.y-offset])
                translate([x,y,size.z-offset])
                    sphere(r=wt, $fn=16);
    }
    difference() {
        cube(size);
        translate([wt,wt,wt])
            cube([size.x-2*wt,size.y-2*wt,size.z-2*wt]);
    }
}

module pairing_button() {
    translate([27,-22-wall_thickness/2+0.1,6])
        color(button_color)
            cube([7,button_depth,5], center=true);
}

module status_led() {
    translate([-24,-22-wall_thickness/2,8])
        color(led_color)
            sphere(d=led_diam-0.2, $fn=16);
}

module usb_c_port() {
    difference() {
        translate([-27.5-wall_thickness/2,0,3.5])
            rotate([0,90,0])
                color(metal_color)
                    cylinder(h=usb_c_depth, d=9, $fn=32);
        // inner cavity
        translate([-27.5-wall_thickness/2-0.1,0,3.5])
            rotate([0,90,0])
                cylinder(h=usb_c_depth+0.2, d=6.7, $fn=32);
    }
}

// --- RENDER -----------------------------------------------------------------
charging_case();

