screen_color = "black";
button_color = "DimGray";
accent_color = "RoyalBlue";

// Main body shell
module body_shell() {
    difference() {
        // Main body
        color(body_color)
        rounded_cube([body_length, body_width, body_thickness], 3);

        // Hollow interior
        translate([wall_thickness, wall_thickness, wall_thickness])
            rounded_cube([body_length-2*wall_thickness, body_width-2*wall_thickness, body_thickness-wall_thickness], 2);

        // Screen cutout
        translate([(body_length-screen_width)/2, (body_width-screen_height)/2, body_thickness-screen_depth])
            cube([screen_width, screen_height, screen_depth+1], center=false);

        // USB-C port
        translate([body_length-usb_depth-1, (body_width-usb_width)/2, body_thickness/2-usb_height/2])
            cube([usb_depth+1, usb_width, usb_height]);
    }
}

// Rounded cube helper
module rounded_cube(size, radius) {
    hull() {
        for (x = [0, 1])
            for (y = [0, 1])
                for (z = [0, 1])
                    translate([x*(size[0]-2*radius), y*(size[1]-2*radius), z*(size[2]-2*radius)])
                        sphere(r=radius);
    }
}

