module screen() {
    color(screen_color)
    translate([(body_length-screen_width)/2, (body_width-screen_height)/2, body_thickness-screen_depth])
        cube([screen_width, screen_height, screen_depth]);

    // Screen bezel
    color("LightGray")
    translate([(body_length-screen_width-2)/2, (body_width-screen_height-2)/2, body_thickness-screen_depth-0.5])
        cube([screen_width+2, screen_height+2, 0.5]);
}

