// Crate for small electronic device (Raspberry Pi Zero style)
// Overall dimensions: 60 x 40 x 25 mm

// Device parameters
device_x = 30;
device_y = 15;
device_thickness = 5;

// Crate parameters
crate_x = 60;
crate_y = 40;
crate_z = 25;
wall_thickness = 2;
floor_thickness = 3;

// Color definitions
crate_color = "BurlyWood";
device_color = "DarkGreen";
screen_color = "Black";
port_color = "Silver";

// Main crate assembly
union() {
    // Main crate body
    color(crate_color) {
        difference() {
            // Outer shell
            cube([crate_x, crate_y, crate_z], center=true);

            // Hollow interior
            translate([0, 0, -floor_thickness/2])
                cube([crate_x-2*wall_thickness, crate_y-2*wall_thickness, crate_z], center=true);
        }
    }

    // Crate lid
    color(crate_color) {
        translate([0, 0, crate_z/2 + 1])
            cube([crate_x, crate_y, 2], center=true);
    }

    // Crate handle cutouts
    for(i = [-1, 1]) {
        color(crate_color) {
            translate([i*crate_x/4, crate_y/2, crate_z/2])
                rotate([0, 90, 0])
                    cylinder(h=10, r=3, center=true);
        }
    }

    // Electronic device inside
    color(device_color) {
        translate([0, 0, -crate_z/2 + floor_thickness + device_thickness/2])
            cube([device_x, device_y, device_thickness], center=true);
    }

    // Device screen
    color(screen_color) {
        translate([0, 0, -crate_z/2 + floor_thickness + device_thickness + 0.1])
            cube([device_x*0.8, device_y*0.6, 0.2], center=true);
    }

    // HDMI port on device
    color(port_color) {
        translate([device_x/2, 0, -crate_z/2 + floor_thickness + device_thickness/2])
            cube([2, 6, 4], center=true);
    }

    // USB port on device
    color(port_color) {
        translate([-device_x/2, 0, -crate_z/2 + floor_thickness + device_thickness/2])
            cube([2, 6, 4], center=true);
    }

    // Ventilation holes in crate sides
    module vent_holes() {
        for(y = [-crate_y/3, 0, crate_y/3]) {
            for(z = [-crate_z/4, 0, crate_z/4]) {
                translate([crate_x/2, y, z])
                    rotate([90, 0, 0])
                        cylinder(h=wall_thickness+0.1, r=1.5, center=true);
            }
        }
    }

    color(crate_color) {
        difference() {
            cube([crate_x, crate_y, crate_z], center=true);
            vent_holes();
            mirror([1, 0, 0]) vent_holes();
        }
    }

    // Power LED indicator
    color("Red") {
        translate([crate_x/2 - 3, crate_y/2 - 3, crate_z/2 - 3])
            sphere(r=1.5);
    }

    // Mounting posts for device
    color(crate_color) {
        for(x = [-device_x/2 + 5, device_x/2 - 5]) {
            for(y = [-device_y/2 + 3, device_y/2 - 3]) {
                translate([x, y, -crate_z/2 + floor_thickness/2])
                    cylinder(h=floor_thickness, r=1.5, center=true);
            }
        }
    }
}

