// Drop-style electronic device - realistic medium complexity
// Features: teardrop body, OLED screen, touch button, USB-C port, LED indicator

// Overall dimensions
drop_length = 80;
drop_width = 45;
drop_thickness = 12;

// Wall thickness for 3D printing
wall_thickness = 1.5;

// Main teardrop body
module teardrop_body() {
    difference() {
        // Outer shell
        color("#E8E8E8") 
        hull() {
            // Top bulb
            translate([0, -drop_length/4, 0])
            sphere(d=drop_width, $fn=50);

            // Bottom tapered point
            translate([0, drop_length/2, 0])
            scale([0.3, 1, 0.3])
            sphere(d=drop_width*0.8, $fn=30);

            // Connect with elongated spheres
            for(i=[-drop_length/4:5:drop_length/2]) {
                translate([0, i, 0])
                scale([1-0.7*(i+drop_length/4)/drop_length, 1, 1-0.7*(i+drop_length/4)/drop_length])
                sphere(d=drop_width*0.9, $fn=30);
            }
        }

        // Hollow interior
        translate([0, 0, wall_thickness])
        scale([(drop_width-wall_thickness*2)/drop_width, 
               (drop_length-wall_thickness*2)/drop_length,
               (drop_thickness-wall_thickness*2)/drop_thickness])
        teardrop_body_solid();
    }
}

// Solid version for hollowing
module teardrop_body_solid() {
    hull() {
        translate([0, -drop_length/4, 0])
        sphere(d=drop_width, $fn=50);

        translate([0, drop_length/2, 0])
        scale([0.3, 1, 0.3])
        sphere(d=drop_width*0.8, $fn=30);

        for(i=[-drop_length/4:5:drop_length/2]) {
            translate([0, i, 0])
            scale([1-0.7*(i+drop_length/4)/drop_length, 1, 1-0.7*(i+drop_length/4)/drop_length])
            sphere(d=drop_width*0.9, $fn=30);
        }
    }
}

