    translate([0, -drop_length/4, drop_thickness/2+0.1])
    cube([drop_width*0.7, drop_length*0.3, 0.5], center=true);

    // Screen surface
    color("#001122")
    translate([0, -drop_length/4, drop_thickness/2+0.6])
    cube([drop_width*0.65, drop_length*0.28, 0.1], center=true);
}

// Capacitive touch button
