// Uses only basic OpenSCAD primitives, transformations, and booleans

// Color palette
$fa = 1; $fs = 1;

module main_body() {
    color("dimgray")
    difference() {
        // Main shell
        translate([-35, -15, 0])
            minkowski() {
                cube([70, 30, 8]);
                sphere(d = 2);
            }

        // USB-C port opening
        translate([28, -13, 4])
            rotate([0, 90, 0])
                cylinder(h = 26, d = 8.4, $fn = 24);

        // Reset button hole
        translate([-28, -13, 4])
            rotate([0, 90, 0])
                cylinder(h = 26, d = 2.2, $fn = 12);
    }
}

module display_area() {
    color("black")
    difference() {
        // Raised bezel
        translate([-20, -8, 8])
            cube([40, 16, 2]);

        // Recessed screen
        translate([-19, -7, 9.8])
            cube([38, 14, 0.5]);
    }
}

module screen_glare() {
    color([0.1, 0.1, 0.1, 0.7])
    translate([-19, -7, 10.3])
        cube([38, 14, 0.1]);
}

module side_buttons() {
    for (y = [-5, 0, 5]) {
        color("silver")
        translate([-35.5, y, 8])
            rotate([90, 0, 0])
                cylinder(h = 3, d = 3, $fn = 12);
    }
}

module belt_clip() {
    color("darkgray")
    difference() {
        // Clip body
        translate([-35, -15, -6])
            minkowski() {
                cube([70, 30, 2]);
                sphere(d = 1);
            }

        // Spring gap
        translate([-20, -15, -5])
            cube([40, 30, 1]);

        // Grip teeth
        for (i = [-2:2])
            translate([i * 10, -15, -5])
                cube([2, 2, 1]);
    }

    // Spring
    color("lightgray")
    translate([0, 0, -5])
        rotate([0, 90, 0])
            cylinder(h = 15, d = 2, $fn = 12);
}

module heart_rate_window() {
    color("darkgreen")
    translate([0, 10, 0])
    difference() {
        // Raised window
        cylinder(h = 1, d = 10, $fn = 32);

        // LED holes
        for (a = [0:120:240])
            rotate([0, 0, a])
                translate([3, 0, -0.1])
                    cylinder(h = 1.2, d = 2, $fn = 12);
    }
}

module logo() {
    color("white")
    translate([20, 0, 9])
    linear_extrude(height = 0.5) {
        text("FIT", size = 4, halign = "center", valign = "center");
    }
}

module charging_contacts() {
    color("gold")
    for (x = [-10, 10])
        translate([x, 13, 0.5])
            cylinder(h = 0.5, d = 3, $fn = 16);
}

// Assemble the complete belt fitness tracker
main_body();
display_area();
screen_glare();
side_buttons();
belt_clip();
heart_rate_window();
logo();
charging_contacts();

// Realistic Radio Holster (Medium Complexity)
// Fits a typical handheld two-way radio (e.g., Baofeng UV-5R style)

// Parameters
wall_th = 2.5;      // wall thickness
clear = 0.5;        // clearance for device
belt_w = 45;        // belt slot width
belt_h = 6;         // belt slot height

// Radio dimensions (UV-5R)
radio_w = 58 + clear;
radio_h = 110 + clear;
radio_d = 32 + clear;

// Holster body
module holster_body() {
    difference() {
        // Outer shell
        rounded_box([radio_w + 2*wall_th, radio_h + 2*wall_th, radio_d + wall_th + 8], 3);

        // Inner cavity
        translate([0, 0, 4])
            rounded_box([radio_w, radio_h, radio_d + 10], 2);

        // Top opening
        translate([0, radio_h/2 + wall_th, 0])
            cube([radio_w + 2*wall_th + 1, wall_th + 1, radio_d + wall_th + 8 + 1], center=true);

        // Antenna hole
        translate([0, -radio_h/2 - 5, radio_d/2 - 5])
            rotate([90, 0, 0])
                cylinder(h=20, d=12, $fn=32);

        // Speaker/mic holes
        for(i = [0:2])
            translate([-radio_w/2 - 1, -radio_h/2 + 20 + i*15, 0])
                rotate([0, 90, 0])
                    cylinder(h=wall_th + 2, d=8, $fn=16);

        // Side button access
        translate([radio_w/2 + 1, 0, 0])
            rotate([0, 90, 0])
                cylinder(h=wall_th + 2, d=20, $fn=32);
    }
}

// Belt clip
module belt_clip() {
    difference() {
        // Main clip body
        translate([0, -radio_h/2 - 15, radio_d/2 + 10])
            rounded_box([belt_w, 30, belt_h + 2*wall_th], 2);

        // Belt slot
        translate([0, -radio_h/2 - 15, radio_d/2 + 10 + wall_th])
            cube([belt_w - 2*wall_th, 25, belt_h], center=true);
    }

    // Retention nubs
    for(i = [-1:1])
        translate([i * 15, -radio_h/2 - 15, radio_d/2 + 10 + belt_h + wall_th])
            sphere(d=3, $fn=16);
}

// Radio representation (for visualization)
module radio_device() {
    color("darkslategray") {
        // Main body
        translate([0, 0, radio_d/2 + 4])
            rounded_box([radio_w - clear, radio_h - clear, radio_d - clear], 2);

        // Display
        color("black")
            translate([0, radio_h/4, radio_d/2 + 4.5])
                cube([40, 20, 0.5], center=true);

        // Keypad
        color("black")
            for(y = [0:2])
                for(x = [0:2])
                    translate([-15 + x*15, -30 - y*15, radio_d/2 + 4.5])
                        cube([8, 8, 0.5], center=true);

        // Antenna
        color("black")
            translate([0, -radio_h/2 + 5, radio_d/2 + 4])
                cylinder(h=60, d=8, $fn=32);

        // Volume knob
        color("black")
            translate([radio_w/2 - 5, 20, radio_d/2 + 4])
                cylinder(h=8, d=16, $fn=32);

        // PTT button
        color("red")
            translate([radio_w/2 - 5, -20, radio_d/2 + 4])
                rounded_box([15, 30, 5], 2);
    }
}

// Helper module for rounded box
module rounded_box(size, r) {
    hull() {
        for(x = [-1, 1])
            for(y = [-1, 1])
                for(z = [-1, 1])
                    translate([x*(size[0]/2 - r), y*(size[1]/2 - r), z*(size[2]/2 - r)])
                        sphere(r=r, $fn=16);
    }
}

// Assembly
color("olive") holster_body();
color("olive") belt_clip();
%radio_device();

