// Main colors
color_body = [0.15, 0.15, 0.15];
color_antenna = [0.8, 0.8, 0.8];
color_ports = [0.2, 0.2, 0.2];
color_led = [0, 1, 0];
color_label = [0.9, 0.9, 0.9];

module main_body() {
    color(color_body)
    union() {
        // Main housing
        translate([0, 0, 10])
        difference() {
            cube([60, 40, 25], center=true);
            // Ventilation slots
            for(i = [-20:10:20])
                translate([i, 0, 12.5])
                cube([8, 38, 2], center=true);
        }

        // Front face
        translate([0, 0, 22.5])
        cube([58, 38, 1], center=true);
    }
}

module antennas() {
    color(color_antenna)
    union() {
        // Left antenna
        translate([-35, -15, 25])
        rotate([0, 0, -30])
        cylinder(h=25, d=4, center=false);

        // Right antenna
        translate([-35, 15, 25])
        rotate([0, 0, 30])
        cylinder(h=25, d=4, center=false);

        // Antenna bases
        translate([-35, -15, 25])
        sphere(d=6);
        translate([-35, 15, 25])
        sphere(d=6);
    }
}

module ports() {
    color(color_ports)
    translate([30, 0, 15])
    union() {
        // Ethernet ports
        for(y = [-10, 0, 10])
            translate([0, y, 0])
            difference() {
                cube([8, 8, 6], center=true);
                translate([4, 0, 0])
                cube([1, 6, 4], center=true);
            }

        // Power port
        translate([0, 15, 0])
        difference() {
            cylinder(h=6, d=10, center=true);
            cylinder(h=7, d=6, center=true);
        }
    }
}

module status_indicators() {
    // LED indicators
    color(color_led)
    translate([25, 0, 22.5])
    union() {
        // Power LED
        translate([0, -15, 0])
        cylinder(h=1, d=3, center=true);

        // Signal strength LEDs
        for(i = [0:2])
            translate([0, -10 + i*5, 0])
            cylinder(h=1, d=2, center=true);
    }

    // WPS button
    color([0.1, 0.1, 0.1])
    translate([25, 15, 22.5])
    cube([5, 5, 1], center=true);
