// OpenSCAD model of a realistic USB-C accelerator dongle (e.g. Google Coral)
// External dimensions: 63 x 23 x 11 mm

// Main body shell
module body() {
    difference() {
        color("black")
        rounded_box([63, 23, 11], 2);  // rounded shell

        // internal cavity for PCB
        color("black") 
        translate([1, 1, 1])
        rounded_box([61, 21, 9], 1.5);
    }
}

// Helper: rounded box primitive
module rounded_box(size, r) {
    hull() {
        for(dx=[0,size.x], dy=[0,size.y], dz=[0,size.z])
        translate([dx, dy, dz]) sphere(r);
    }
}

// USB-C plug
module usb_c_plug() {
    translate([-6, 0, 0])
    difference() {
        union() {
            color("silver") {
                // metal shell
                cube([12, 15.5, 5], center=true);
            }
            color("white") {
                // plastic tongue
                translate([0, 0, -1.5])
                cube([6.5, 7, 2.6], center=true);
            }
        }
        // cavity inside plug
        translate([0, 0, -1.5])
        cube([7, 8, 3], center=true);
    }
}

// Status LED indicator
module status_led() {
    translate([55, 5, 1])
    color("green")
    cylinder(h = 1, d = 3, $fn = 20);
}

// Ventilation slots on sides
module vents() {
    for(y=[-1,1]) {
        translate([30, y*11, 5.5])
        rotate([90, 0, 0])
        for(i=[-8:8]) {
            translate([i*3, 0, 0])
            color("black")
            cube([1.5, 0.6, 1]);
        }
    }
}

// Engraved logo area
module logo() {
    translate([52, 0, 11.01])
    color("white")
    linear_extrude(0.3)
    text("AI", size=6, font="Arial:style=Bold", halign="center", valign="center");
}

// Mounting posts for PCB
module pcb_posts() {
    for(x=[10, 53], y=[5, 18]) {
        translate([x, y, 1])
        color("black")
        cylinder(h = 9, d = 2.2, $fn = 12);
    }
}

// Main assembly
union() {
    body();
    usb_c_plug();
    status_led();
    vents();
    logo();
    pcb_posts();
}

// Electronic Fuel Injector Model
// Realistic style with medium complexity
// Approx 75mm length, suitable for 3D printing

$fn = 50;  // Smooth curves
module injector() {
    // Main body - cylindrical shape
    color("silver") 
    cylinder(h=50, r1=10, r2=8, center=true);

    // Top connector - electrical plug
    translate([0,0,25]) 
    color("black")
    difference() {
        cylinder(h=15, r=6, center=true);
        translate([0,0,-8]) 
        cylinder(h=10, r=4, center=true);
    }

    // O-ring grooves (3x)
    for(z=[-15,0,15]) {
        translate([0,0,z]) 
        color("gray")
        difference() {
            cylinder(h=2, r=10.5, center=true);
            cylinder(h=3, r=9.5, center=true);
        }
    }

    // Fuel inlet ports (4x)
    for(angle=[0:90:270]) {
        rotate([0,0,angle]) 
        translate([0,10,10]) 
        rotate([90,0,0]) 
        color("darkgray")
        cylinder(h=8, r=3, center=true);
    }

    // Nozzle tip
    translate([0,0,-30]) {
        color("silver")
        cylinder(h=15, r1=8, r2=3, center=true);

        // Spray holes (micro holes)
        for(angle=[0:60:300]) {
            rotate([0,0,angle]) 
            translate([2,0,-35]) 
            color("black")
            cylinder(h=2, r=0.5, center=true);
        }
    }

    // Solenoid housing
    translate([0,0,15]) 
    color("darkblue") 
    difference() {
        cylinder(h=10, r=12, center=true);
        translate([0,0,-6]) 
        cylinder(h=12, r=8, center=true);
    }

    // Electrical pins (2x)
    for(x=[-2,2]) {
        translate([x,0,32]) 
        color("gold")
        cylinder(h=5, r=1, center=true);
    }

    // Mounting bracket
    translate([12,0,0]) 
    rotate([0,90,0]) 
    color("lightgray")
    difference() {
        cube([20,8,5], center=true);
        translate([0,0,0]) 
        cylinder(h=6, r=2.5, center=true);
    }

    // Part number label
    translate([0,11,5]) 
    rotate([90,0,0]) 
    color("white")
    linear_extrude(height=0.5) 
    text("EV1-0280", size=2, halign="center");

    // Flow direction arrow
    translate([0,-11,5]) 
    rotate([90,0,180]) 
    color("red")
    linear_extrude(height=0.5) 
    text("â†’", size=4, halign="center");
}

// Render the injector
injector();

