color_screen = [0, 0, 0];         // Black
color_label = [0.8, 0.8, 0.8];    // Light gray

// Create the main enclosure
module enclosure() {
    color(color_enclosure)
    difference() {
        // Main body
        cube([length, width, height], center = true);

        // Hollow out the inside
        translate([0, 0, 1])
        cube([length - 2*wall_thickness, width - 2*wall_thickness, height - 1], center = true);

        // Port cutouts on front
        translate([length/2 + 1, -15, 0])
        rotate([0, 90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);

        translate([length/2 + 1, 0, 0])
        rotate([0, 90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);

        translate([length/2 + 1, 15, 0])
        rotate([0, 90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);

        // Port cutouts on back
        translate([-length/2 - 1, -15, 0])
        rotate([0, -90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);

        translate([-length/2 - 1, 0, 0])
        rotate([0, -90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);

        translate([-length/2 - 1, 15, 0])
        rotate([0, -90, 0])
        cylinder(h = 10, d1 = 8, d2 = 6);
    }
}

// LED indicators
module leds() {
    for(x = [-25, -15, -5, 5, 15, 25]) {
        color(color_led)
        translate([x, width/2 - 2, height/2 - 1])
        cylinder(h = 1, d = 3);
    }
}

// Ventilation holes
module vents() {
    for(i = [-3:3]) {
        for(j = [-1:1]) {
            translate([i * 10, j * 10, 0])
            cube([2, 2, height/2], center = true);
        }
    }
}

// Control buttons
module buttons() {
    color(color_buttons)
    // Reset button
    translate([length/2 - 8, width/2 - 5, height/2 - 1])
    cylinder(h = 2, d = 4);

    // Mode button
    translate([length/2 - 8, -width/2 + 5, height/2 - 1])
    cylinder(h = 2, d = 4);
}

// LCD screen
module screen() {
    color(color_screen)
    translate([0, 0, height/2 - 0.5])
    cube([30, 12, 1]);

    // Screen bezel
    color(color_enclosure)
    translate([0, 0, height/2])
    difference() {
        cube([32, 14, 0.5], center = true);
        translate([0, 0, -0.1])
        cube([30, 12, 0.7], center = true);
    }
}

// Mounting brackets
module brackets() {
    color(color_enclosure)
    // Left bracket
    translate([-length/2 - 5, 0, -height/2 + 2])
    cube([10, 30, 4]);

    // Right bracket
    translate([length/2 + 5, 0, -height/2 + 2])
    cube([10, 30, 4]);

    // Mounting holes
    color([1, 1, 1])
    translate([-length/2 - 5, 10, -height/2 + 2])
    cylinder(h = 5, d = 3);

    translate([-length/2 - 5, -10, -height/2 + 2])
    cylinder(h = 5, d = 3);

    translate([length/2 + 5, 10, -height/2 + 2])
    cylinder(h = 5, d = 3);

    translate([length/2 + 5, -10, -height/2 + 2])
    cylinder(h = 5, d = 3);
}

// Label area
module label() {
    color(color_label)
    translate([0, width/2 + 1, 0])
    cube([40, 2, 8], center = true);
}

