// 3D-printable 8Ã—8 LED Array Module â€“ realistic & medium complexity
// Overall size: 80 mm Ã— 80 mm Ã— 25 mm (fits most FDM printers)

// ---- PARAMETERS ----
pitch   = 9.5;     // center-to-center LED spacing
module_w = 8 * pitch;
module_h = 8 * pitch;
wall_t  = 2.0;     // printable wall thickness
led_d   = 5.0;     // LED diameter
led_h   = 1.5;     // LED protrusion
pcb_t   = 1.6;     // PCB thickness
case_h  = 12;      // enclosure depth
rib_w   = 1.0;     // grid rib width
screw_d = 3.0;     // mounting screw diameter
screw_h = 5.0;     // screw insert depth

// ---- MAIN MODULE ----
module led_array() {
    difference() {
        union() {
            case_shell();
            led_grid();
            pcb_base();
            mounting_bosses();
        }

        // LED cavities (grid holes)
        translate([0, 0, case_h - pcb_t - led_h])
            led_cavities();

        // Screw holes
        for (x = [-1, 1], y = [-1, 1])
            translate([x * (module_w/2 - 6), y * (module_h/2 - 6), -1])
                cylinder(h = case_h + 2, d = screw_d);
    }
}

// ---- ENCLOSURE ----
module case_shell() {
    color("black")
    linear_extrude(height = case_h, convexity = 10)
    difference() {
        offset(r = wall_t)
            square([module_w, module_h], center = true);
        square([module_w, module_h], center = true);
    }
}

// ---- LED GRID ----
module led_grid() {
    color("darkgray")
    for (ix = [0:7], iy = [0:7])
        translate([(ix - 3.5) * pitch, (iy - 3.5) * pitch, case_h - pcb_t])
            cylinder(h = led_h + pcb_t, d = led_d + 2 * wall_t, $fn = 32);
}

// ---- LED CAVITIES ----
module led_cavities() {
    for (ix = [0:7], iy = [0:7])
        translate([(ix - 3.5) * pitch, (iy - 3.5) * pitch, 0])
            cylinder(h = led_h + 1, d = led_d, $fn = 32);
}

// ---- PCB BASE ----
module pcb_base() {
    color("darkgreen")
    translate([0, 0, 0])
        cube([module_w - 2 * wall_t, module_h - 2 * wall_t, pcb_t], center = true);
}

// ---- MOUNTING BOSSES ----
module mounting_bosses() {
    color("black")
    for (x = [-1, 1], y = [-1, 1])
        translate([x * (module_w/2 - 6), y * (module_h/2 - 6), case_h - screw_h])
            cylinder(h = screw_h, d = 6, $fn = 20);
}

// ---- RENDER ----
led_array();

