// Bus Electronic Device - Realistic Style with Medium Complexity
// Model represents a handheld ticket validation device used on buses

// Color definitions
color_body = [0.2, 0.2, 0.2];       // Dark gray main body
color_screen = [0.1, 0.1, 0.1];     // Black screen
color_keys = [0.4, 0.4, 0.4];       // Medium gray keys
color_port = [0.3, 0.3, 0.3];       // USB port color
color_indicator = [0, 1, 0];        // Green LED
color_label = [1, 1, 1];            // White label

// Main dimensions
device_length = 70;
device_width = 35;
device_height = 12;
wall_thickness = 1.5;

// Screen dimensions
screen_width = 25;
screen_height = 15;
screen_depth = 0.5;

// Key dimensions
key_width = 5;
key_height = 5;
key_depth = 0.8;

// USB port dimensions
port_width = 8;
port_height = 3;
port_depth = 4;

module main_body() {
    color(color_body)
    difference() {
        // Main body shell
        cube([device_length, device_width, device_height], center=true);

        // Hollow out the inside
        translate([0, 0, -wall_thickness/2])
            cube([device_length - 2*wall_thickness, 
                  device_width - 2*wall_thickness, 
                  device_height], center=true);

        // Top rounding
        translate([0, 0, device_height/2 - 2])
            cube([device_length - 4, device_width - 4, 4], center=true);
    }

    // Rounded corners using spheres
    for(x = [-1, 1]) {
        for(y = [-1, 1]) {
            translate([x*(device_length/2 - 2), y*(device_width/2 - 2), 0])
                sphere(r=2, $fn=16);
        }
    }
}

module screen() {
    color(color_screen)
    translate([0, 0, device_height/2 - screen_depth/2])
        cube([screen_width, screen_height, screen_depth], center=true);

    // Screen bezel
    color(color_body)
    translate([0, 0, device_height/2 - screen_depth/2 - 0.1])
        cube([screen_width + 2, screen_height + 2, 0.2], center=true);
}

module keypad() {
    // Key layout - 3x4 grid
    for(row = [0:2]) {
        for(col = [0:3]) {
            translate([
                -device_length/3 + col * key_width * 1.2,
                device_width/4 - row * key_height * 1.2,
                device_height/2 + key_depth/2
            ]) {
                color(color_keys)
                cube([key_width, key_height, key_depth], center=true);

                // Key spacing
                translate([0, 0, -0.1])
                    color(color_body)
                    cube([key_width + 0.5, key_height + 0.5, 0.2], center=true);
            }
        }
    }
}

module usb_port() {
    color(color_port)
    translate([device_length/2 - port_depth/2, 0, 0])
        cube([port_depth, port_width, port_height], center=true);

    // Port opening
    difference() {
        translate([device_length/2 - port_depth/2, 0, 0])
            cube([port_depth + 0.5, port_width, port_height], center=true);

        translate([device_length/2 + port_depth/2, 0, 0])
