// Tree-shaped electronic device - realistic style
// A bonsai-style tree that functions as a USB hub and decorative LED lamp

module tree_trunk() {
    // Main trunk body
    color([0.4, 0.2, 0.1]) {
        // Base cylinder
        cylinder(h=12, d1=15, d2=12, $fn=20);

        // Second segment
        translate([0, 0, 12])
            cylinder(h=10, d1=12, d2=10, $fn=20);

        // Third segment with slight bend
        translate([2, 1, 22])
            rotate([15, 0, 10])
            cylinder(h=8, d1=10, d2=8, $fn=20);
    }
}

module branches() {
    // Generate 5 main branches
    for(i = [0:4]) {
        angle = i * 72 + 30;
        rotate([0, 0, angle]) {
            translate([6, 0, 28])
                rotate([30 - i*3, 0, 0])
                branch_segment(8, 2.5);
        }
    }
}

module branch_segment(length, thickness) {
    // Recursive branch generation
    color([0.3, 0.15, 0.08]) {
        cylinder(h=length, d=thickness, $fn=8);

        // Add sub-branches if large enough
        if(length > 4) {
            translate([0, 0, length/2])
                rotate([0, 90, 45])
                branch_segment(length*0.7, thickness*0.7);

            translate([0, 0, length/2])
                rotate([0, -90, -45])
                branch_segment(length*0.7, thickness*0.7);
        }
    }
}

module leaves() {
    // LED leaves at branch tips
    for(i = [0:14]) {
        branch_num = floor(i/3);
        leaf_pos = i % 3;

        rotate([0, 0, branch_num * 72 + 30]) {
            translate([8 + leaf_pos*2, 0, 32 + leaf_pos*1.5])
                rotate([35 - branch_num*3, 0, 0])
                led_leaf();
        }
    }
}

module led_leaf() {
    // Individual LED leaf with wiring
    color([0.1, 0.8, 0.2]) {
        // Leaf body
        scale([1, 0.3, 0.1])
            sphere(d=4, $fn=12);

        // LED indicator
        color([0.2, 1, 0.2])
            translate([1.5, 0, 0.2])
                sphere(d=0.8, $fn=8);

        // Copper trace
        color([0.8, 0.5, 0.2])
            translate([0, 0, -0.1])
                cube([3, 0.1, 0.05]);
    }
}

module base() {
    // Electronic base with ports
    color([0.2, 0.2, 0.2]) {
        difference() {
            // Main base
            cylinder(h=8, d=35, $fn=32);

            // USB ports - 4 around the base
            for(i = [0:3]) {
                rotate([0, 0, i*90])
                    translate([15, 0, 4])
                        cube([6, 2, 4], center=true);
            }

            // Power LED indicator
            translate([10, 0, 7.5])
                cylinder(h=1, d=3, $fn=16);

            // Micro USB power port
            translate([0, 15, 4])
                cube([7, 2, 4], center=true);
        }

        // Power button
        color([0.1, 0.1, 0.1])
            translate([0, -15, 8])
                cylinder(h=2, d=6, $fn=20);

        // Base label
        color([0.9, 0.9, 0.9])
            translate([0, 0, 8.1])
                linear_extrude(0.1)
                    text("USB Tree Hub", size=3, halign="center");
    }
}

module roots() {
    // Decorative cable management roots
    color([0.3, 0.15, 0.08]) {
        for(i = [0:5]) {
            angle = i * 60;
            rotate([0, 0, angle])
                translate([8, 0, 0])
                    rotate([90, 0, 0])
                        cylinder(h=5 + i, d=2, $fn=6);
        }
    }
}

// Main assembly
union() {
    // Electronic base
    base();

    // Cable roots
    translate([0, 0, 0])
        roots();

    // Tree trunk
    translate([0, 0, 8])
        tree_trunk();

    // Branches
    branches();

    // LED leaves
    leaves();
}

