// OpenSCAD code for a realistic supervisor electronic device
// Dimensions in mm, suitable for 3D printing

wall_thickness = 2;
corner_radius = 3;

// Main body dimensions
body_w = 60;
body_h = 40;
body_d = 25;

// Screen dimensions
screen_w = 48;
screen_h = 24;
screen_d = 1;

// Button dimensions
button_r = 3;
button_h = 1.5;

// LED indicator dimensions
led_r = 1.5;
led_h = 1;

// USB port dimensions
usb_w = 12;
usb_h = 5;
usb_d = 4;

// Antenna dimensions
antenna_r = 2;
antenna_h = 15;

// Color definitions
$fn = 32;

module rounded_cube(size, r) {
    translate([r, r, 0])
    minkowski() {
        cube([size.x-2*r, size.y-2*r, size.z]);
        cylinder(r=r, h=0.01);
    }
}

module main_body() {
    color([0.2, 0.2, 0.2]) // Dark gray plastic
    difference() {
        rounded_cube([body_w, body_h, body_d], corner_radius);

        // Screen cutout
        translate([(body_w-screen_w)/2, (body_h-screen_h)/2, body_d-screen_d])
        cube([screen_w, screen_h, screen_d+0.1]);

        // USB port cutout
        translate([0, (body_h-usb_h)/2, (body_d-usb_d)/2])
        cube([usb_d+0.1, usb_h, usb_d]);

        // Ventilation slots
        for(i = [0:4]) {
            translate([body_w - 5, 5 + i*6, 2])
            cube([wall_thickness, 3, body_d-4]);
        }
    }
}

module screen() {
    color([0.1, 0.1, 0.1]) // Black screen
    translate([(body_w-screen_w)/2, (body_h-screen_h)/2, body_d-screen_d+0.01])
    cube([screen_w, screen_h, screen_d]);
}

module buttons() {
    // Power button
    color([0.8, 0.1, 0.1]) // Red button
    translate([10, 5, body_d+0.1])
    cylinder(r=button_r, h=button_h);

    // Mode button
    color([0.1, 0.8, 0.1]) // Green button
    translate([20, 5, body_d+0.1])
    cylinder(r=button_r, h=button_h);

    // Reset button (recessed)
    color([0.8, 0.8, 0.1]) // Yellow button
    translate([body_w-10, 5, body_d-button_h-0.5])
    cylinder(r=button_r-0.5, h=button_h);
}

module led_indicators() {
    // Status LED
    color([0, 1, 0]) // Green LED
    translate([body_w-15, body_h-8, body_d-0.5])
    cylinder(r=led_r, h=led_h);

    // Error LED
    color([1, 0, 0]) // Red LED
    translate([body_w-8, body_h-8, body_d-0.5])
    cylinder(r=led_r, h=led_h);
}

module usb_port() {
    color([0.4, 0.4, 0.4]) // Metal USB port
    translate([usb_d/2, body_h/2, body_d/2])
    rotate([0, 90, 0])
    difference() {
        cube([usb_w, usb_h, usb_d]);
        translate([2, 2, -0.1])
        cube([usb_w-4, usb_h-4, usb_d+0.2]);
    }
}

module antenna() {
    color([0.7, 0.7, 0.7]) // Metal antenna
    translate([body_w/2, body_h, body_d/2])
    rotate([0, 90, 0])
    cylinder(r=antenna_r, h=antenna_h);

    // Antenna base
    color([0.3, 0.3, 0.3])
    translate([body_w/2, body_h, body_d/2])
    sphere(r=antenna_r+1);
}

module mounting_holes() {
    // Mounting holes on back
    for(x = [10, body_w-10])
    for(y = [10, body_h-10]) {
        translate([x, y, 0])
        cylinder(r=1.5, h=wall_thickness+0.1, $fn=6);
    }
}

// Assembly
union() {
    difference() {
        union() {
            main_body();
            screen();
        }
        mounting_holes();
    }

    buttons();
    led_indicators();
    usb_port();
    antenna();
}

// FabricModule Electronic Device - Realistic Style
// A modular synthesizer module with fabric-inspired texture

// Color definitions
module_color = [0.25, 0.25, 0.25];  // Dark gray aluminum
panel_color = [0.15, 0.15, 0.15];   // Matte black
screen_color = [0.1, 0.9, 0.1];     // Green LCD
led_color = [1, 0, 0];              // Red LED
knob_color = [0.8, 0.8, 0.8];       // Silver

// Overall dimensions
module_width = 40;  // Eurorack width (HP * 5.08mm)
module_height = 128.5;  // Eurorack height
module_depth = 25;

// Wall thickness
wall = 2;

module fabric_texture(h, w, d) {
    difference() {
        cube([w, d, h]);

        // Create fabric-like indentations
        for (x = [0 : 2 : w-2]) {
            for (y = [0 : 2 : d-2]) {
                translate([x+1, y+1, h-0.5])
                    cube([1.5, 1.5, 1.5], center=true);
            }
        }
    }
}

module eurorack_module() {
    // Main module body
    difference() {
        color(module_color)
            cube([module_width, module_depth, module_height]);

        // Hollow out the inside
        translate([wall, wall, wall])
            cube([module_width-wall*2, module_depth-wall*2, module_height-wall*2]);
    }

    // Front panel
    color(panel_color)
        translate([0, 0, module_height-4])
            cube([module_width, module_depth, 4]);
}

module display() {
    // LCD screen
    color(screen_color)
        translate([module_width/2-12, module_depth-6, module_height-2])
            cube([24, 4, 2]);

    // Screen bezel
    color([0.1, 0.1, 0.1])
        translate([module_width/2-13, module_depth-7, module_height-3])
            cube([26, 6, 0.5]);
}

module knob(x_pos, y_pos, label) {
    // Knob base
    color([0.2, 0.2, 0.2])
        translate([x_pos, y_pos, module_height-1])
            cylinder(h=3, r=4, $fn=20);

    // Knob cap
    color(knob_color)
        translate([x_pos, y_pos, module_height+1.5])
            cylinder(h=4, r=3.5, $fn=20);

    // Knob indicator line
    color([0, 0, 0])
        translate([x_pos, y_pos+3.4, module_height+3])
            cube([0.5, 1, 0.2]);
}

module button(x_pos, y_pos, state) {
    // Button housing
    color([0.1, 0.1, 0.1])
        translate([x_pos, y_pos, module_height-1])
            cube([6, 6, 2]);

    // Button
    color(state ? led_color : [0.3, 0.3, 0.3])
        translate([x_pos+1, y_pos+1, module_height+0.5])
            cube([4, 4, 1.5]);
}

module jack(x_pos, y_pos, type) {
    // Jack socket
    color([0.1, 0.1, 0.1])
        translate([x_pos, y_pos, module_height-1])
            cylinder(h=3, r=3.5, $fn=20);

    // Jack hole
    color([0, 0, 0])
        translate([x_pos, y_pos, module_height-2])
            cylinder(h=5, r=2.5, $fn=20);

    // Jack ring
    color([0.8, 0.8, 0.8])
        translate([x_pos, y_pos, module_height+1])
            cylinder(h=0.5, r=4, $fn=20);
}

module led_indicator(x_pos, y_pos, color_val) {
    // LED bezel
    color([0.1, 0.1, 0.1])
        translate([x_pos, y_pos, module_height-0.5])
            cylinder(h=1, r=2, $fn=20);

    // LED
    color(color_val)
        translate([x_pos, y_pos, module_height])
            sphere(r=1.5, $fn=20);
}

module power_header() {
    // 10-pin power header
    translate([wall+3, wall+3, module_height/2])
        rotate([0, 90, 0])
            for (i = [0:9]) {
                translate([i*2.54-11.43, 0, 0])
                    cylinder(h=5, r=0.635, $fn=8);
            }
}

// Main assembly
eurorack_module();

// Add fabric texture to sides
translate([0, 0, wall])
    fabric_texture(module_height-wall*2, module_width, wall);

translate([0, module_depth-wall, wall])
    fabric_texture(module_height-wall*2, module_width, wall);

// Front panel components
display();

// Knobs (3 across)
knob(module_width/2-12, module_depth-12, "FREQ");
knob(module_width/2, module_depth-12, "RES");
knob(module_width/2+12, module_depth-12, "AMP");

// Buttons
button(module_width/2-8, module_depth-28, false);
button(module_width/2+8, module_depth-28, true);

// Jacks
jack(module_width/2-15, module_depth-20, "IN");
jack(module_width/2, module_depth-20, "CV");
jack(module_width/2+15, module_depth-20, "OUT");

// LED indicators
led_indicator(module_width/2-5, module_depth-35, led_color);
led_indicator(module_width/2+5, module_depth-35, [0, 1, 0]);

// Module name text area
color([0.05, 0.05, 0.05])
    translate([module_width/2-15, module_depth-2, module_height-1])
        cube([30, 2, 1]);

// Power header on back
power_header();

// OpenSCAD Switch Module - Realistic Style, Medium Complexity
// A 3D printable DIN-rail mountable industrial switch module with 4 channels

module main_body() {
    color("gray40")
    union() {
        // Main enclosure base
        difference() {
            cube([72, 90, 35], center=true);
            translate([0, 0, 17.5])
            cube([68, 86, 35], center=true);
        }

        // DIN rail clip (bottom)
        translate([0, 0, -17])
        difference() {
            cube([60, 80, 4], center=true);
            translate([0, 0, 2])
            cube([58, 78, 4], center=true);
        }
    }
}

module switch_rocker(pos, color_name) {
    translate(pos)
    rotate([10, 0, 0])
    color(color_name)
    union() {
        difference() {
            cube([20, 12, 6], center=true);
            translate([0, 0, 3])
            cube([18, 10, 6], center=true);
        }
        // Rocker pivot
        translate([0, 0, -3.5])
        rotate([90, 0, 0])
        cylinder(h=12, d=1.5, center=true);
    }
}

module led_indicator(pos, color_name) {
    translate(pos)
    union() {
        color("black")
        cylinder(h=2, d=6);
        translate([0, 0, 2.1])
        color(color_name)
        sphere(d=5);
    }
}

module terminal_block(pos) {
    translate(pos)
    color("gray20")
    union() {
        cube([8, 20, 12], center=true);
        // Screw terminals
        for(y = [-7.5, -2.5, 2.5, 7.5]) {
            translate([0, y, 6.5])
            rotate([0, 0, 0])
            cylinder(h=3, d=3, $fn=12);
        }
    }
}

module side_label() {
    translate([36, 0, 0])
    rotate([0, 90, 0])
    color("white")
    linear_extrude(height=0.1)
    text("SWITCH-MOD-4CH", size=4, halign="center", valign="center");
}

module cable_gland(pos, angle) {
    translate(pos)
    rotate([0, 0, angle])
    union() {
        color("black")
        cylinder(h=8, d=12);
        translate([0, 0, 4])
        cylinder(h=4, d=14);
        // Internal threading approximation
        for(i = [0:5:359]) {
            rotate([0, 0, i])
            translate([5.5, 0, 0])
            cube([1, 0.5, 8]);
        }
    }
}

