// Realistic Line Interface Device - OpenSCAD
// Medium complexity 3D printable model

// Color definitions
$fn = 50;

// Main dimensions
device_w = 80;
device_h = 20;
device_d = 50;

// Wall thickness
wall_t = 2;

// Create the main enclosure
module enclosure() {
    difference() {
        // Main body
        color("darkslategray")
        rounded_box(device_w, device_h, device_d, 3);

        // Hollow out
        translate([0, 0, wall_t/2])
            rounded_box(device_w-wall_t*2, device_h-wall_t*2, device_d-wall_t, 2);
    }
}

// Rounded box helper
module rounded_box(w, h, d, r) {
    hull() {
        for(x = [-1, 1])
        for(y = [-1, 1])
        for(z = [-1, 1])
            translate([x*(w/2-r), y*(h/2-r), z*(d/2-r)])
                sphere(r=r);
    }
}

// Display screen
module display() {
    translate([0, device_h/2 - 1, 0])
        color("black")
        cube([45, 0.5, 20], center=true);

    // Screen bezel
    translate([0, device_h/2 - 0.8, 0])
        color("gray20")
        cube([47, 0.3, 22], center=true);

    // Screen content (simplified lines)
    for(i = [-9:2:9]) {
        translate([i*2, device_h/2 - 0.5, 0])
            color("limegreen")
            cube([0.5, 0.1, 18], center=true);
    }
}

// Control buttons
module control_buttons() {
    // Power button
    translate([device_w/2 - 10, device_h/2 - 0.5, device_d/2 - 8])
        rotate([90, 0, 0])
        cylinder(h=1.5, d=4);

    // Menu buttons
    for(i = [0:2]) {
        translate([device_w/2 - 10, device_h/2 - 0.5, device_d/2 - 15 - i*5])
            rotate([90, 0, 0])
            color("darkred")
            cylinder(h=1.5, d=3);
    }
}

// LED indicators
module indicators() {
    // Status LEDs
    for(i = [0:1]) {
        translate([-device_w/2 + 5 + i*8, device_h/2 - 0.5, device_d/2 - 5])
            rotate([90, 0, 0])
            color(i == 0 ? "green" : "red")
            cylinder(h=1, d=2);
    }
}

// Connection ports
module ports() {
    // Ethernet port
    translate([-device_w/2 + 15, 0, device_d/2])
        difference() {
            color("gray30")
            cube([12, 8, 4]);
            translate([6, 0, 2])
                cube([8, 6, 2], center=true);
        }

    // USB ports
    for(i = [0:1]) {
        translate([-device_w/2 + 35 + i*12, 0, device_d/2])
            difference() {
                color("gray30")
                cube([8, 6, 4]);
                translate([4, 0, 2])
                    cube([6, 4, 2], center=true);
            }
    }

    // Power jack
    translate([-device_w/2 + 5, 0, device_d/2])
        difference() {
            color("gray30")
            cylinder(h=4, d=8);
            cylinder(h=5, d=4);
        }
}

// Ventilation slots
module vents() {
    for(i = [0:3]) {
        translate([device_w/2 - 5 - i*3, -device_h/4, 0])
            rotate([0, 90, 0])
            color("gray20")
            cube([device_d - 10, 1, 0.5]);
    }
}

// Mounting holes
module mounting_holes() {
    for(x = [-1, 1])
    for(z = [-1, 1])
        translate([x*device_w/3, -device_h/2, z*device_d/3])
            rotate([90, 0, 0])
            cylinder(h=5, d=4);
}

// Assembly
union() {
    enclosure();

    translate([0, 0, 0]) {
        display();
        control_buttons();
        indicators();
        vents();
    }

    // Ports on side
    translate([0, 0, 0])
        ports();

    // Mounting holes (subtractive)
    difference() {
        children();
        mounting_holes();
    }
}

// USB-C hub / port interface â€“ 3D-printable, medium realism
// Approx. 70 mm Ã— 24 mm Ã— 14 mm
// No overhangs >45Â°, 1 mm wall thickness

$fn = 48;               // smoother curves
wall = 1.2;             // printable wall thickness
corner = 1.5;           // edge-rounding radius

module rounded_box(size, r) {
    // simple rounded box for body
    hull() {
        for(dx=[0,size.x], dy=[0,size.y], dz=[0,size.z])
            translate([dx,dy,dz])
                sphere(r);
    }
}

module body() {
    difference() {
        // outer shell
        translate([corner,corner,corner])
            rounded_box([70-2*corner,24-2*corner,14-2*corner], corner);
        // hollow interior
        translate([corner+wall,corner+wall,corner+wall])
            rounded_box([70-2*(corner+wall),24-2*(corner+wall),14-2*(corner+wall)], corner-wall);
    }
}

module usb_c_port(pos) {
