// PHY-Ethernet Switch (8-port desktop model)
// Overall dimensions: 90 x 60 x 25 mm (realistic 8-port switch scaled for printing)

// Color constants
color_case = [0.2, 0.2, 0.2];  // Dark grey plastic
color_ports = [0.4, 0.4, 0.4]; // Medium grey
color_leds = [0.1, 0.9, 0.1];  // Green
color_label = [0.9, 0.9, 0.9]; // Light grey
color_metal = [0.5, 0.5, 0.5]; // Aluminum

// Main case body
module switch_case() {
    difference() {
        // Outer shell with rounded edges
        minkowski() {
            cube([90, 60, 20], center=true);
            sphere(3);
        }

        // Hollow out inside
        translate([0, 0, 2])
            cube([86, 56, 20], center=true);

        // Ventilation slots on top
        for (y = [-20:10:20]) {
            translate([0, y, 10])
                cube([80, 2, 3], center=true);
        }
    }
}

// Front panel with ports
module front_panel() {
    color(color_case) {
        translate([0, -30, 0])
            cube([90, 2, 15], center=true);
    }

    // 8 Ethernet ports
    for (i = [0:7]) {
        translate([-35 + i*10, -30, -2]) {
            // Port opening
            color(color_ports)
                difference() {
                    cube([8, 2, 8], center=true);
                    translate([0, 0, 2])
                        cube([6, 3, 6], center=true);
                }

            // LED indicator for each port
            translate([0, -1, 4])
                color(color_leds)
                    sphere(1);
        }
    }

    // Activity LEDs
    for (i = [0:3]) {
        translate([-40 + i*20, -30, 5])
            color(color_leds)
                cylinder(h=1, r=1.5);
    }
}

// Rear panel
