    color(color_case) {
        translate([0, 30, 0])
            cube([90, 2, 15], center=true);
    }

    // Power jack
    translate([30, 30, -2])
        difference() {
            cylinder(h=3, r=4);
            cylinder(h=4, r=2);
        }

    // Uplink port
    translate([-30, 30, -2]) {
        color(color_ports)
            difference() {
                cube([8, 2, 8], center=true);
                translate([0, 0, 2])
                    cube([6, 3, 6], center=true);
            }
