// OpenSCAD 3D-printable NIC (Network Interface Card) â€“ medium-realistic style
// Overall size approx 110 mm Ã— 68 mm Ã— 18 mm â€“ fits common printer beds
// All walls â‰¥ 1.2 mm, no steep overhangs

$fa = 1; $fs = 0.5;

// ---------- color constants ----------
pcb_col   = [0.18,0.28,0.12];
bracket_col = [0.75,0.75,0.75];
chip_col  = [0.15,0.15,0.15];
screen_col = [0.1,0.1,0.1];
led_col   = [0,1,0];
gold_col  = [0.8,0.65,0.2];

// ---------- main assembly ----------
module nic_card() {
    union() {
        color(pcb_col) pcb();
        color(bracket_col) bracket();
        color(chip_col) main_chip();
        color(chip_col) small_chips();
        color(gold_col) gold_fingers();
        color(led_col) leds();
        color(screen_col) activity_display();
        screw_holes();  // negative
    }
}

// ---------- PCB ----------
module pcb() {
    difference() {
        union() {
            // main board body
            translate([0,0,0]) cube([110,68,1.6], center=true);
            // raised RJ45 area
            translate([42,0,1.6/2+7.5/2]) cube([26,16,7.5], center=true);
        }
        // keep-out zone for bracket cut-out
        translate([-110/2-1,0,-2]) cube([20,20,4], center=true);
    }
}

// ---------- bracket ----------
module bracket() {
    difference() {
        // bracket plate
        translate([-110/2,0,9]) cube([2,68,18], center=true);
        // screw slots
        for (y = [-28,28])
            translate([-110/2-1,y,9]) rotate([0,90,0]) cylinder(h=4,d=3.2,center=true,$fn=20);
    }
}

// ---------- main Ethernet controller chip ----------
module main_chip() {
    translate([-15,0,1.6/2+2.5/2]) cube([25,25,2.5], center=true);
}

// ---------- auxiliary chips and caps ----------
module small_chips() {
    // flash
    translate([35,25,1.6/2+1.5/2]) cube([12,8,1.5], center=true);
    // voltage regulator
    translate([35,-25,1.6/2+1.5/2]) cube([10,6,1.5], center=true);
    // SMD capacitors (row)
    for (x = [-40:10:-10])
        translate([x,-10,1.6/2+1.2/2]) cube([3,2,1.2], center=true);
}

// ---------- gold fingers (edge fingers) ----------
module gold_fingers() {
    for (i = [0:7]) {
        translate([110/2-3, -34+4.5+i*7.8, -1.6/2-0.2/2])
            cube([6,1.2,0.2]);
    }
}

// ---------- activity LEDs ----------
module leds() {
    translate([42,5,1.6/2+7.5+1.5]) cylinder(h=1.5,d=3,center=true,$fn=16);
    translate([42,-5,1.6/2+7.5+1.5]) cylinder(h=1.5,d=3,center=true,$fn=16);
}

// ---------- RJ45 port ----------
module activity_display() {
    difference() {
        // RJ45 opening
        translate([42,0,1.6/2+7.5-2]) cube([14,16,4], center=true);
        // 8-pin slot
        translate([42,0,1.6/2+7.5-2-0.5]) cube([12,2,1], center=true);
    }
}

// ---------- screw holes for fixing the PCB ----------
module screw_holes() {
    for (p = [[-45,-25],[45,-25],[45,25],[-45,25]])
        translate([p[0],p[1],0]) cylinder(h=1.6+0.2,d=3.2,center=true,$fn=20);
}

