// OpenSCAD parametric transoptor (optocoupler) model
// Realistic style, medium complexity

$fn = 50;  // Smooth curves

// Main dimensions
base_width = 12;
base_length = 8;
base_height = 3.5;
pin_diameter = 0.8;
pin_length = 10;
led_diameter = 5;
led_height = 1.5;
phototransistor_diameter = 5;
phototransistor_height = 1.5;
wall_thickness = 1;
slot_width = 1.2;
slot_depth = 1;

module base() {
    // Main black epoxy body
    color([0.12, 0.12, 0.12]) 
    difference() {
        // Main body
        cube([base_width, base_length, base_height], center = true);

        // Side chamfers for realistic look
        translate([base_width/2 - 1, 0, 0]) rotate([0, 0, 45]) 
            cube([2, base_length + 2, base_height + 2], center = true);
        translate([-base_width/2 + 1, 0, 0]) rotate([0, 0, -45]) 
            cube([2, base_length + 2, base_height + 2], center = true);
    }

    // Top surface detail - slight recess
    translate([0, 0, base_height/2 - 0.2])
    color([0.08, 0.08, 0.08])
    cube([base_width - 1, base_length - 1, 0.4], center = true);
}

module pins() {
    // 4 pins - standard DIP spacing
    color([0.8, 0.8, 0.8])
    for(i = [0:1:3]) {
        translate([-base_width/2 + 2.54 + i*2.54, -base_length/2 - pin_length/2, 0])
            cylinder(h = pin_length, d = pin_diameter, center = true);
    }

    // Pin widening at base for strength
    color([0.7, 0.7, 0.7])
    for(i = [0:1:3]) {
        translate([-base_width/2 + 2.54 + i*2.54, -base_length/2, 0])
            cube([pin_diameter + 0.4, 1, pin_diameter + 0.4], center = true);
    }
}

module light_barrier() {
    // Slot between LED and phototransistor
    color([0.05, 0.05, 0.05])
    translate([0, 0, 0])
    cube([slot_width, base_length - 1, slot_depth + 0.5], center = true);
}

module led() {
    // IR LED dome
    translate([-base_width/4, 0, base_height/2 + led_height/2])
    color([0.9, 0.1, 0.1])  // Red IR LED
    sphere(d = led_diameter);

    // LED substrate
    translate([-base_width/4, 0, base_height/2])
    color([0.3, 0.3, 0.3])
    cylinder(h = 0.3, d = led_diameter + 0.5, center = true);
}

module phototransistor() {
    // Phototransistor dome
    translate([base_width/4, 0, base_height/2 + phototransistor_height/2])
    color([0.1, 0.1, 0.9])  // Blue detector
    sphere(d = phototransistor_diameter);

    // Phototransistor substrate
    translate([base_width/4, 0, base_height/2])
    color([0.3, 0.3, 0.3])
    cylinder(h = 0.3, d = phototransistor_diameter + 0.5, center = true);
}

module dot_indicator() {
    // Pin 1 indicator dot
    translate([-base_width/2 + 0.8, base_length/2 - 0.8, base_height/2 + 0.1])
    color([1, 1, 1]) 
    sphere(d = 0.5);
}

module transoptor() {
    union() {
        base();
        pins();
        light_barrier();
        led();
        phototransistor();
        dot_indicator();
    }
}

// Render the complete transoptor
transoptor();

