// Rounded box helper
module rounded_box(w, d, h, r) {
    hull() {
        translate([r-w/2, r-d/2, r])
            cylinder(h=h-2*r, r=r);
        translate([w/2-r, r-d/2, r])
            cylinder(h=h-2*r, r=r);
        translate([r-w/2, d/2-r, r])
            cylinder(h=h-2*r, r=r);
        translate([w/2-r, d/2-r, r])
            cylinder(h=h-2*r, r=r);
    }
    translate([0, 0, h/2])
        cube([w, d, h-2*r], center=true);
}

