// DAS (Direct Attached Storage) Electronic Device
// Realistic 3D printable model for a portable SSD enclosure

$fn = 64;

// Main dimensions
enclosure_length = 70;
enclosure_width = 40;
enclosure_height = 10;
wall_thickness = 1.5;
corner_radius = 3;

// Color definitions
color_enclosure = [0.15, 0.15, 0.15];
color_ports = [0.8, 0.8, 0.8];
color_led = [0, 1, 0];
color_label = [0.9, 0.9, 0.9];

module rounded_cube(size, radius) {
    hull() {
        translate([radius, radius, radius])
            sphere(r=radius);
        translate([size.x - radius, radius, radius])
            sphere(r=radius);
        translate([size.x - radius, size.y - radius, radius])
            sphere(r=radius);
        translate([radius, size.y - radius, radius])
            sphere(r=radius);
        translate([radius, radius, size.z - radius])
            sphere(r=radius);
        translate([size.x - radius, radius, size.z - radius])
            sphere(r=radius);
        translate([size.x - radius, size.y - radius, size.z - radius])
            sphere(r=radius);
        translate([radius, size.y - radius, size.z - radius])
            sphere(r=radius);
    }
}

// Main enclosure body
module enclosure_body() {
    color(color_enclosure) {
        difference() {
            rounded_cube([enclosure_length, enclosure_width, enclosure_height], corner_radius);

            // Hollow out interior
            translate([wall_thickness, wall_thickness, wall_thickness])
                rounded_cube([
                    enclosure_length - 2 * wall_thickness,
                    enclosure_width - 2 * wall_thickness,
                    enclosure_height - wall_thickness
                ], corner_radius - wall_thickness);
        }
    }
}

// USB-C port
module usb_c_port() {
    color(color_ports) {
        // Port opening
        translate([enclosure_length - wall_thickness, enclosure_width/2 - 4, wall_thickness])
            cube([wall_thickness, 8, 3]);

        // Port connector detail
        translate([enclosure_length - wall_thickness + 0.2, enclosure_width/2 - 3, wall_thickness + 0.5])
            cube([0.5, 6, 2]);
    }
}

// Activity LED
module activity_led() {
    color(color_led) {
        translate([enclosure_length - 10, wall_thickness + 0.1, enclosure_height/2])
            cylinder(h=0.2, r=1.5);
    }
}

// Brand label area
module brand_label() {
    color(color_label) {
        translate([enclosure_length/2 - 15, enclosure_width/2 - 5, enclosure_height - 0.2])
            cube([30, 10, 0.2]);
    }
}

// Ventilation slots
module ventilation_slots() {
    color(color_enclosure) {
        for(i = [0:4]) {
            translate([10 + i * 12, wall_thickness - 0.1, enclosure_height - 3])
                cube([8, 0.2, 2]);
        }
    }
}

// Assembly screw holes
module screw_holes() {
    color(color_enclosure) {
        // Bottom screw holes
        translate([corner_radius + 5, corner_radius + 5, 0])
            cylinder(h=wall_thickness, r=1, $fn=16);
        translate([enclosure_length - corner_radius - 5, corner_radius + 5, 0])
            cylinder(h=wall_thickness, r=1, $fn=16);
        translate([corner_radius + 5, enclosure_width - corner_radius - 5, 0])
            cylinder(h=wall_thickness, r=1, $fn=16);
        translate([enclosure_length - corner_radius - 5, enclosure_width - corner_radius - 5, 0])
            cylinder(h=wall_thickness, r=1, $fn=16);
    }
}

