color_screen = [0.1, 0.1, 0.1];
color_led = [0, 1, 0];
color_ports = [0.3, 0.3, 0.3];
color_logo = [0.8, 0.8, 0.8];

// Main case
module main_case() {
    color(color_case)
    difference() {
        // Outer shell
        cube([device_width, device_depth, device_height], center = true);

        // Inner cavity
        translate([0, 0, -wall_thickness/2])
            cube([device_width - 2*wall_thickness, device_depth - 2*wall_thickness, device_height + 1], center = true);

        // Ventilation slots on top
        for (x = [-30:10:30]) {
            translate([x, device_depth/2 - 5, device_height/2 - 2])
                cube([3, 3, 4]);
        }
    }
}

// Front panel with screen and indicators
module front_panel() {
    // Screen area
    color(color_screen)
    translate([0, device_depth/2 - 0.5, 5])
        cube([50, 1, 15], center = true);

    // Status LEDs
    for (i = [0:2]) {
        color(color_led)
        translate([-20 + i*15, device_depth/2 - 0.5, -5])
            cylinder(h = 1, r = 2);
    }

    // Power LED
    color([1, 0, 0])
    translate([30, device_depth/2 - 0.5, 5])
        cylinder(h = 1, r = 1.5);
}

// Side ports
module side_ports() {
    // USB ports
    for (i = [0:1]) {
        color(color_ports)
        translate([-device_width/2 + 0.5, -15 + i*10, -5])
            cube([2, 8, 5]);
    }

    // Ethernet port
    color(color_ports)
    translate([-device_width/2 + 0.5, 10, 5])
        cube([2, 12, 8]);

    // Power connector
    color(color_ports)
    translate([device_width/2 - 0.5, 0, 5])
        cube([2, 10, 10]);
}

// Cooling vents on bottom
module cooling_vents() {
    difference() {
        translate([0, 0, -device_height/2 + 1])
            cube([device_width - 10, device_depth - 10, 2], center = true);

        for (x = [-30:5:30]) {
            for (y = [-20:5:20]) {
                translate([x, y, -device_height/2 + 0.5])
                    cube([2, 2, 3]);
            }
        }
    }
}

// Logo on top
module logo() {
    color(color_logo)
    translate([0, 0, device_height/2 - 0.5])
    linear_extrude(height = 0.5) {
        text("FILER", size = 8, halign = "center", valign = "center");
    }

    translate([0, -12, device_height/2 - 0.5])
    linear_extrude(height = 0.5) {
        text("CLOUD", size = 6, halign = "center", valign = "center");
    }
}

// Rubber feet
module rubber_feet() {
    color([0.1, 0.1, 0.1])
    for (x = [-30, 30]) {
        for (y = [-20, 20]) {
            translate([x, y, -device_height/2])
                cylinder(h = 2, r = 3);
        }
    }
}

// Antenna
