// FilerLink â€“ a miniature Network-Attached-Storage (NAS) box
// 60 Ã— 60 Ã— 40 mm, 3D-printable, no support required
// Medium-complexity, realistic style
// Colors: dark grey enclosure, blue status LED, black feet

$fa = 1;
$fs = 0.4;

// Main dimensions ----------------------------------------------------------
case_w  = 60;
case_d  = 60;
case_h  = 30;
wall_t  = 2.0;
corner_r = 4;

// Ports --------------------------------------------------------------------
usb_w   = 12;
usb_h   = 4.5;
eth_w   = 14;
eth_h   = 10;

// Feet ---------------------------------------------------------------------
foot_d  = 5;
foot_h  = 3;

// LED ----------------------------------------------------------------------
led_r   = 2;

module rounded_box(w, d, h, r) {
    // solid rounded box with vertical edges filleted
    hull() {
        for(x = [r, w-r], y = [r, d-r])
            translate([x, y, r]) sphere(r=r);
        translate([0,0,h-r]) cube([w,d,0], center=false);
    }
}

module case_shell() {
    // hollow enclosure with open bottom
    difference() {
        rounded_box(case_w, case_d, case_h, corner_r);
        translate([wall_t, wall_t, wall_t])
            rounded_box(case_w-2*wall_t, case_d-2*wall_t,
                        case_h-wall_t, corner_r-wall_t);
    }
}

module port_row() {
    // horizontal row: USB-A, RJ45, USB-C
    translate([0, 0, case_h/2]) {
        // USB-A
        translate([-(usb_w/2 + 2), 0, 0])
            cube([usb_w, wall_t+0.1, usb_h], center=true);
        // Ethernet
        translate([0, 0, 0])
            cube([eth_w, wall_t+0.1, eth_h], center=true);
        // USB-C
        translate([(eth_w/2 + 2 + usb_w/2), 0, 0])
            cube([usb_w, wall_t+0.1, usb_h], center=true);
    }
}

module vent_holes() {
    // top cooling vents (horizontal slits)
    for(y = [-20:5:20])
        translate([0, y, case_h-1])
            cube([case_w-10, 2, 2], center=true);
}

module status_led() {
    color("blue")
    translate([case_w/2-8, case_d/2-4, case_h-wall_t-0.1])
        cylinder(h=0.5, r=led_r, center=false);
}

module rubber_feet() {
    // four small cylindrical feet
    color("black")
    for(x = [foot_d, case_w-foot_d], y = [foot_d, case_d-foot_d])
        translate([x, y, -foot_h/2])
            cylinder(h=foot_h, r=foot_d/2, center=true, $fn=16);
}

module filerlink_logo() {
    // simple raised text block
    translate([case_w/2-15, case_d/2-10, case_h-0.2])
        cube([20, 5, 0.4]);
}

// Assembly -----------------------------------------------------------------
difference() {
    case_shell();
    translate([0, case_d-wall_t/2, 0])
        rotate([0, 90, 0])
            port_row();
    translate([0, 0, case_h])
