// OpenSCAD model of a realistic 2.5" SSD disk
// Dimensions based on a typical 7 mm thick 2.5" drive

// Overall dimensions
drive_w = 69.85;  // Standard 2.5" width
drive_l = 100.0;  // Standard 2.5" length
drive_h = 7.0;    // Slim 7 mm height

// Wall thickness for 3D printing
wall = 1.2;

// Color constants
color_case = "gray60";
color_label = "white";
color_screw = "gray30";
color_port = "gold";
color_led = "lime";

// Main body shell
module drive_case() {
    difference() {
        // Outer shell
        color(color_case)
        cube([drive_l, drive_w, drive_h], center=true);

        // Hollow out the inside
        translate([0, 0, -wall/2])
            cube([drive_l-wall*2, drive_w-wall*2, drive_h-wall], center=true);

        // Rounded corners on bottom
        for(x=[-drive_l/2+wall, drive_l/2-wall]) {
            for(y=[-drive_w/2+wall, drive_w/2-wall]) {
                translate([x, y, -drive_h/2+wall/2])
                    cylinder(r=wall, h=drive_h, center=true);
            }
        }
    }
}

// Label sticker on top
module top_label() {
    color(color_label)
    translate([0, 0, drive_h/2-0.1])
        cube([drive_l-6, drive_w-6, 0.2], center=true);
}

// SATA data + power connector
module sata_connector() {
    color(color_port)
    translate([drive_l/2-1.5, 0, -drive_h/2+2.5])
        cube([3, 28, 5]);

    // SATA data pins
    translate([drive_l/2-1, -12, -drive_h/2+2.5+2.5])
        for(i=[0:6])
            translate([0, i*3.8, 0])
                cube([0.6, 1.4, 0.6]);

    // SATA power pins
    translate([drive_l/2-1, 12, -drive_h/2+2.5+2.5])
        for(i=[0:14])
            translate([0, -i*1.27, 0])
                cube([0.6, 0.8, 0.6]);
}

// Mounting screw holes (4 corners)
module screw_holes() {
    color(color_screw)
    for(x=[-drive_l/2+8, drive_l/2-8]) {
        for(y=[-drive_w/2+3, drive_w/2-3]) {
            translate([x, y, 0])
                cylinder(r=1.5, h=drive_h, center=true);
        }
    }
}

// Activity LED indicator
module activity_led() {
    color(color_led)
    translate([drive_l/2-5, drive_w/2-5, drive_h/2-0.5])
        sphere(r=0.6);
}

// Bottom case features
module bottom_features() {
    // Ventilation slots
    color(color_case)
    for(x=[-drive_l/2+20:5:drive_l/2-20]) {
        translate([x, 0, -drive_h/2+wall/2])
            cube([2, drive_w-wall*2, wall]);
    }

    // Regulatory markings
    translate([0, -drive_w/2+5, -drive_h/2+0.1])
        cube([12, 8, 0.2], center=true);
}

// Assembly
drive_case();
top_label();
sata_connector();
difference() {
    union();  // Placeholder for positive geometry
    screw_holes();
}
activity_led();
bottom_features();

