// Realistic Medium-Complexity 3D-Printable Steak
// Dimensions: ~75 mm x 55 mm x 12 mm

// Overall color: medium-rare reddish-brown
color([0.55, 0.25, 0.20]) {

    // Main steak body with organic irregular shape
    difference() {
        // Base slab â€“ slightly tapered toward edges
        hull() {
            for (i = [0:7]) {
                translate([
                    35 * sin(i * 45) + 5 * sin(i * 90),
                    25 * cos(i * 45) + 3 * cos(i * 120),
                    0
                ])
                sphere(d = 10, $fn = 12);
            }
        }
        // Thickness
        linear_extrude(height = 12) offset(delta = -2) {
            projection() hull() {
                for (i = [0:7]) {
                    translate([
                        35 * sin(i * 45) + 5 * sin(i * 90),
                        25 * cos(i * 45) + 3 * cos(i * 120),
                        0
                    ])
                    circle(d = 10, $fn = 12);
                }
            }
        }
    }

    // Fat marbling veins
    color([0.95, 0.90, 0.85]) {
        for (ang = [0:30:150]) {
            rotate([0, 0, ang])
            translate([0, 0, 6])
            hull() {
                for (t = [0:0.2:1]) {
                    translate([
                        25 * t - 10,
                        10 * sin(t * 25) - 5,
                        0
                    ])
                    sphere(d = 1.2 + 0.8 * sin(t * 18), $fn = 8);
                }
            }
        }
    }

    // Grill/sear stripes (raised ridges)
    color([0.35, 0.15, 0.10]) {
        for (y = [-20:5:20]) {
            translate([0, y, 12])
            rotate([90, 0, 0])
            cylinder(h = 1.5, d1 = 3, d2 = 2.5, $fn = 6);
        }
    }

    // Slight edge curl for realism
    difference() {
        translate([0, 0, 0])
        scale([1, 1, 1])
        children();
        translate([0, 0, -1])
        scale([0.92, 0.92, 1.2])
        children();
    }
}

