// Realistic Chicken Drumstick - 3D Printable Food Model
// Dimensions: ~75mm total height

$fn = 32;
wall_thickness = 2;

// Main colors
chicken_color = "#E8C39E";
bone_color = "#F5F5DC";
seasoning_color = "#8B4513";

// Seasoning speckle module
module seasoning_speckle(pos_x, pos_y, pos_z) {
    translate([pos_x, pos_y, pos_z])
        scale([0.8, 0.8, 0.3])
            sphere(r = 0.5);
}

// Create the meat texture with bumps and indentations
module meat_texture() {
    difference() {
        // Main meat body
        color(chicken_color)
        sphere(r = 20);

        // Random indentations for realistic texture
        for(i = [0:20]) {
            rotate([rand(i*5)*360, rand(i*7)*360, 0])
                translate([rand(i*3)*15-7.5, rand(i*11)*15-7.5, rand(i*13)*10-5])
                    sphere(r = rand(i*17)*2 + 1);
        }
    }

    // Add small bumps for crispy texture
    for(i = [0:35]) {
        rotate([rand(i*23)*360, rand(i*29)*360, 0])
            translate([rand(i*31)*18-9, rand(i*37)*18-9, rand(i*41)*18-9])
                scale([1.2, 1.2, 0.6])
                    sphere(r = 0.8);
    }
}

// Bone handle
module bone_handle() {
    color(bone_color)
    union() {
        // Main bone cylinder
        translate([0, 0, 25])
            cylinder(h = 35, r1 = 6, r2 = 4);

        // Knobby end
        translate([0, 0, 60])
            sphere(r = 6);

        // Bone texture rings
        for(i = [0:4]) {
            translate([0, 0, 28 + i*6])
                rotate([0, 0, 45])
                    cylinder(h = 0.5, r = 6.2);
        }
    }
}

// Seasoning coating
module seasoning_coating() {
    color(seasoning_color)
    for(i = [0:50]) {
        rotate([rand(i*43)*360, rand(i*47)*360, 0])
            translate([rand(i*53)*35-17.5, rand(i*59)*35-17.5, rand(i*61)*35-17.5])
                seasoning_speckle(0, 0, 0);
    }
}

// Main assembly
union() {
    // Position meat on bone
    translate([0, 0, 5])
        rotate([0, 0, 15])
            meat_texture();

    // Bone handle
    bone_handle();

    // Add seasoning coating
    translate([0, 0, 5])
        seasoning_coating();
}

// Helper function for pseudo-random numbers
function rand(n) = abs(sin(n*123.456))*2;

// Fish food pellet - realistic style with medium complexity
// A realistic fish food pellet with textured surface and food coloring

// Main module for the fish food pellet
module fish_food_pellet() {
    // Main pellet body - slightly flattened ellipsoid
    color([0.85, 0.65, 0.25])  // Golden yellow color
    scale([1, 0.7, 0.4])
    sphere(r = 10, $fn = 32);

    // Add surface texture with small bumps
    for(i = [0:15]) {
        rotate([rand(0, 360), rand(0, 360), rand(0, 360)])
        translate([rand(-8, 8), rand(-6, 6), rand(-3, 3)])
        color([0.75, 0.55, 0.15])
        sphere(r = 0.8, $fn = 8);
    }

    // Create spiral ridges around the pellet
    for(angle = [0:30:360]) {
        rotate([0, 0, angle])
        translate([8 * cos(angle * 3), 6 * sin(angle * 3), 0])
        color([0.9, 0.7, 0.3])
        sphere(r = 1.2, $fn = 6);
    }

    // Add nutritional particles (green and red specs)
    for(i = [0:8]) {
        rotate([rand(0, 180), rand(0, 180), rand(0, 180)])
        translate([rand(-7, 7), rand(-5, 5), rand(0, 1)])
        color([0.2, 0.6, 0.3])  // Green algae particles
        sphere(r = 0.6, $fn = 6);
    }

    for(i = [0:5]) {
        rotate([rand(0, 180), rand(0, 180), rand(0, 180)])
        translate([rand(-6, 6), rand(-4, 4), rand(-1, 0)])
        color([0.7, 0.2, 0.2])  // Red protein particles
        sphere(r = 0.7, $fn = 6);
    }

    // Bottom flat surface for stable printing
    translate([0, 0, -4])
    color([0.85, 0.65, 0.25])
    cylinder(h = 0.5, r1 = 10, r2 = 9.5, $fn = 32);
}

// Helper function for random values
function rand(min, max) = min + (max - min) * (rands(0, 1, 1)[0]);

// Create a small pile of pellets
translate([0, 0, 0])
fish_food_pellet();

translate([22, 0, 0])
rotate([0, 0, 15])
fish_food_pellet();

translate([11, 18, 0])
rotate([0, 0, -25])
fish_food_pellet();

// Realistic pork chop with bone and fat marbling
// Dimensions: ~60mm x 45mm x 12mm

$fn = 32;

module pork_chop() {
    color("#E8B5A2") {
        // Main meat portion
        difference() {
            // Base shape
            translate([0, 0, -6])
                scale([1, 0.8, 0.3])
                    sphere(r = 30);

            // Bone cutout
            translate([-25, 0, -7])
                rotate([0, 90, 0])
                    cylinder(h = 50, r1 = 5, r2 = 7);

            // Taper edges
            translate([0, 0, 2])
                scale([0.85, 0.65, 0.2])
                    sphere(r = 35);
        }

        // Fat layer on edges
        color("#FFF5E1") {
            hull() {
                translate([25, 0, -3])
                    sphere(r = 8);
                translate([-25, 0, -3])
                    sphere(r = 8);
                translate([0, 20, -3])
                    sphere(r = 8);
                translate([0, -20, -3])
                    sphere(r = 8);
            }
        }
    }

    // Bone
    color("#F5F5DC") {
        translate([-27, 0, -5])
            rotate([0, 90, 0])
                cylinder(h = 55, r1 = 4, r2 = 6);

        // Bone end
        translate([-30, 0, -5])
            rotate([0, 90, 0])
                scale([1, 1, 0.5])
                    sphere(r = 6);
    }

    // Fat marbling
    color("#FFF5E1") {
        for(i = [1:15]) {
            translate([
                -20 + 40 * rands(0, 1, 1)[0],
                -15 + 30 * rands(0, 1, 1)[0],
                -2 + 4 * rands(0, 1, 1)[0]
            ]) {
                scale([1, 0.3, 0.1])
                    sphere(r = 1 + rands(0, 2, 1)[0]);
            }
        }
    }

    // Meat grain texture
    color("#D4A082") {
        for(i = [1:30]) {
            translate([
                -20 + 40 * rands(0, 1, 1)[0],
                -15 + 30 * rands(0, 1, 1)[0],
                -3 + 6 * rands(0, 1, 1)[0]
            ]) {
                rotate([0, 90, rands(0, 360, 1)[0]])
                    scale([0.1, 0.1, 0.5])
                        cylinder(h = 5, r = 0.3);
            }
        }
    }
}

pork_chop();

// Realistic Beef Steak - Medium Complexity
// Dimensions: ~80mm x 60mm x 25mm

color([0.6, 0.3, 0.2]) {
    // Main steak body with organic shape
    difference() {
        // Base steak shape - elongated ellipsoid
        scale([1.2, 1, 0.6])
            sphere(d=50, $fn=40);

        // Taper one end to make it steak-shaped
        translate([20, 0, -15])
            rotate([0, 90, 0])
                cylinder(h=50, d1=40, d2=0, $fn=30);
    }

    // Fat marbling - white streaks
    color([0.95, 0.9, 0.85]) {
        for(i = [0:7]) {
            rotate([0, 90, i*25])
                translate([0, 0, -5 + i*2])
                    scale([1.2, 0.1, 0.05])
                        sphere(d=40, $fn=20);
        }

        // Additional fat pockets
        translate([-10, 8, 3])
            sphere(d=6, $fn=15);
        translate([5, -10, 2])
            sphere(d=4, $fn=15);
        translate([15, 12, 1])
            sphere(d=5, $fn=15);
    }
}

// Grill marks on top surface
color([0.4, 0.2, 0.1]) {
    module grill_mark(x, y, rot) {
        translate([x, y, 12])
            rotate([0, 0, rot])
                cube([20, 2, 1], center=true);
    }

    for(i = [0:4]) {
        grill_mark(-15 + i*8, 0, 0);
    }
    for(i = [0:3]) {
        grill_mark(-10 + i*8, 0, 90);
    }
}

// Juicy surface texture - small bumps
color([0.5, 0.25, 0.15]) {
    module juice_bump(x, y, z) {
        translate([x, y, z])
            sphere(d=1.5, $fn=8);
    }

    for(x = [-20:5:20]) {
        for(y = [-15:5:15]) {
            juice_bump(x + rand(-2,2), y + rand(-2,2), 12 + rand(0,0.5));
        }
    }
}

// Edge crust - darker on sides
color([0.35, 0.15, 0.1]) {
    difference() {
        // Slightly larger shell for crust
        scale([1.25, 1.05, 0.65])
            sphere(d=50, $fn=40);

        // Remove interior to leave only crust
        scale([1.15, 0.95, 0.6])
            sphere(d=50, $fn=40);
    }
}

// Bottom flat surface for stable printing
translate([0, 0, -12])
    cube([80, 60, 2], center=true);

// Realistic 3D printable lamb leg roast
// Approximate size: 80 mm length

// Main color definitions
$fa = 2;
$fs = 0.5;

module lamb_leg() {
    color([0.95, 0.75, 0.65]) {
        // Main meat body - tapered cylinder
        translate([0, 0, 5]) {
            cylinder(h = 60, r1 = 25, r2 = 18, center = false);
        }

        // Shank bone end - smaller cylinder
        translate([0, 0, 65]) {
            cylinder(h = 15, r = 12, center = false);
        }

        // Fat cap on top
        color([0.98, 0.85, 0.75]) {
            translate([0, 0, 5]) {
                difference() {
                    cylinder(h = 8, r = 25.5, center = false);
                    cylinder(h = 9, r = 22, center = false);
                }
            }
        }
    }
}

// Meat texture pattern using small spheres
module meat_texture() {
    color([0.85, 0.65, 0.55]) {
        for (i = [0:8]) {
            rotate([0, 0, i * 45]) {
                translate([15 * cos(i * 45), 15 * sin(i * 45), 30]) {
                    sphere(r = 3);
                }
            }
        }
        for (j = [0:6]) {
            rotate([0, 0, j * 60]) {
                translate([20 * cos(j * 60), 20 * sin(j * 60), 45]) {
                    sphere(r = 2.5);
                }
            }
        }
    }
}

// Herb crust details
module herb_crust() {
    color([0.4, 0.3, 0.15]) {
        // Rosemary sprigs
        for (k = [0:4]) {
            rotate([90, 0, k * 72]) {
                translate([15, 0, 20 + k * 10]) {
                    cylinder(h = 8, r = 0.5, center = false);
                }
            }
        }

        // Garlic pieces
        for (m = [0:3]) {
            rotate([0, 0, m * 90]) {
                translate([18 * cos(m * 90), 18 * sin(m * 90), 35]) {
                    sphere(r = 1.5);
                }
            }
        }
    }
}

// Cooking twine wraps
module twine_wraps() {
    color([0.5, 0.3, 0.1]) {
        for (z = [15:20:55]) {
            rotate_extrude(angle = 360) {
                translate([26, 0, 0]) {
                    circle(r = 0.8);
                }
            }
        }
    }
}

// Render the complete lamb leg
union() {
    lamb_leg();
    meat_texture();
    herb_crust();
    twine_wraps();
}

// Realistic 3D printable turkey food model
// Overall dimensions: ~65mm x 45mm x 40mm

// Color definitions
color_turkey = "#D2691E";
color_turkey_dark = "#8B4513";
color_bone = "#FFF8DC";
color_skin = "#F4A460";

// Main turkey body
module turkey_body() {
    color(color_turkey) {
        union() {
            // Main breast
            translate([0, 0, 15]) {
                scale([1, 0.8, 1]) {
                    sphere(d=40);
                }
            }

            // Back portion
            translate([0, 15, 10]) {
                scale([1.2, 0.6, 0.8]) {
                    sphere(d=35);
                }
            }

            // Wings
            // Left wing
            translate([-20, 5, 12]) {
                rotate([0, 0, 30]) {
                    scale([0.8, 0.3, 0.5]) {
                        sphere(d=20);
                    }
                }
            }

            // Right wing
            translate([20, 5, 12]) {
                rotate([0, 0, -30]) {
                    scale([0.8, 0.3, 0.5]) {
                        sphere(d=20);
                    }
                }
            }
        }
    }
}

// Drumstick module
module drumstick(side) {
    color(color_turkey_dark) {
        translate([side * 15, -15, 5]) {
            rotate([90, 0, 0]) {
                union() {
                    // Meat portion
                    scale([0.8, 0.8, 1.2]) {
                        sphere(d=18);
                    }

                    // Bone
                    translate([0, 0, 12]) {
                        color(color_bone) {
                            cylinder(h=20, d=5, center=true);
                        }
                    }
                }
            }
        }
    }
}

// Texture lines for turkey skin
module skin_texture() {
    color(color_skin) {
        for(i = [-15:5:15]) {
            translate([i, 0, 35]) {
                rotate([90, 0, 0]) {
                    scale([0.05, 0.05, 1]) {
                        cylinder(h=20, d=3);
                    }
                }
            }
        }

        for(j = [-10:5:10]) {
            translate([0, j, 35]) {
                rotate([0, 90, 0]) {
                    scale([0.05, 0.05, 1]) {
                        cylinder(h=20, d=3);
                    }
                }
            }
        }
    }
}

// Main assembly
union() {
    // Base turkey body
    turkey_body();

    // Drumsticks
    drumstick(1);  // Right
    drumstick(-1); // Left

    // Skin texture details
    skin_texture();

    // Bottom support base
    translate([0, 0, -2]) {
        color(color_turkey_dark) {
            cylinder(h=2, d=50);
        }
    }
}

// Realistic Duck-Shaped Bread Roll (medium complexity)
// Inspired by Japanese Anpan - sweet bread roll with red bean paste

// Overall dimensions: ~65mm x ~45mm x ~35mm

$fn = 32;

// Main bread body
module bread_body() {
    color("#E8B36B")  // Golden-brown bread color
    union() {
        // Main oval body
        scale([1, 0.8, 0.6])
        sphere(20);

        // Head
        translate([20, 0, 5])
        scale([0.7, 0.7, 0.7])
        sphere(15);

        // Neck connection
        translate([10, 0, -2])
        cylinder(h=15, r1=12, r2=8);
    }
}

// Duck bill
module duck_bill() {
    color("#FF8C42")  // Orange color
    translate([32, 0, 2])
    rotate([0, -20, 0])
    scale([1.2, 0.6, 0.4])
    cube([12, 8, 6], center=true);
}

// Eyes (sesame seeds)
module sesame_seed(loc) {
    color("#2C1810")  // Dark brown sesame color
    translate(loc)
    scale([0.4, 0.4, 1])
    sphere(1.5);
}

// Bread texture pattern
module bread_texture() {
    color("#D4A574")
    difference() {
        // Main body for texture
        bread_body();

        // Small indentations for realistic bread texture
        for(i = [0:45]) {
            rotate([0, 0, i*8])
            translate([15*sin(i*12), 15*cos(i*12), 8])
            sphere(1.5);
        }

        // Surface imperfections
        for(i = [0:25]) {
            rotate([0, 0, i*15])
            translate([10+8*sin(i*5), 8*cos(i*5), 10])
            sphere(0.8);
        }
    }
}

// Wings as bread folds
module wings() {
    color("#E8B36B")
    union() {
        // Left wing
        translate([-5, 15, 0])
        rotate([0, 0, -30])
        scale([1, 0.3, 0.2])
        sphere(12);

        // Right wing
        translate([-5, -15, 0])
        rotate([0, 0, 30])
        scale([1, 0.3, 0.2])
        sphere(12);
    }
}

// Tail as bread end
module tail() {
    color("#D4A574")
    translate([-22, 0, -3])
    rotate([0, 20, 0])
    scale([0.8, 1, 0.5])
    cylinder(h=15, r1=10, r2=6);
}

// Assembly
union() {
    // Main textured body
    bread_texture();

    // Bill
    duck_bill();

    // Wings
    wings();

    // Tail
    tail();

    // Eyes
    sesame_seed([28, 4, 8]);
    sesame_seed([28, -4, 8]);

    // Additional sesame seeds on body
    for(i = [0:8]) {
        rotate([0, 0, i*40])
        sesame_seed([5 + 10*cos(i), 10*sin(i), 12]);
    }
}

// Realistic Salmon Fillet OpenSCAD Model
// Medium complexity - 3D printable

// Color definitions
color_flesh = "#FF6B6B";
color_skin = "#D2691E";
color_fat = "#FFF8DC";

// Main fillet module
module salmon_fillet() {
    // Base fillet shape - tapered ellipsoid
    translate([0, 0, 5])
    scale([1, 0.7, 0.4])
    sphere(20, $fn=50);

    // Tail section - thinner
    translate([-15, 0, 0])
    scale([0.7, 0.5, 0.25])
    sphere(15, $fn=40);

    // Thicker head section
    translate([10, 0, 0])
    scale([1.2, 0.8, 0.5])
    sphere(18, $fn=45);
}

// Skin layer with scales
module skin_layer() {
    color(color_skin) {
        // Main skin surface
        translate([0, 0, 9])
        scale([1.02, 0.72, 0.42])
        sphere(20, $fn=50);

        // Tail skin
        translate([-15, 0, 4])
        scale([0.72, 0.52, 0.27])
        sphere(15, $fn=40);

        // Head skin
        translate([10, 0, 6])
        scale([1.22, 0.82, 0.52])
        sphere(18, $fn=45);
    }
}

// Fat layer between skin and flesh
module fat_layer() {
    color(color_fat) {
        translate([0, 0, 8.5])
        scale([1.01, 0.71, 0.41])
        sphere(20, $fn=50);

        translate([-15, 0, 3.5])
        scale([0.71, 0.51, 0.26])
        sphere(15, $fn=40);

        translate([10, 0, 5.5])
        scale([1.21, 0.81, 0.51])
        sphere(18, $fn=45);
    }
}

// Fish flakes texture
module flakes() {
    color(color_flesh) {
        for(i = [-20:5:20]) {
            for(j = [-8:4:8]) {
                if(i*i + j*j < 400) {
                    translate([i, j, 5])
                    rotate([90, 0, 45])
                    scale([0.1, 1, 0.05])
                    cylinder(h=10, r=1, $fn=6);
                }
            }
        }
    }
}

// Skin texture pattern (scales)
module scales() {
    color(color_skin) {
        for(i = [-20:3:20]) {
            for(j = [-10:3:10]) {
                if(i*i + j*j < 400) {
                    translate([i, j, 9.5])
                    rotate([0, 90, 0])
                    scale([0.5, 0.3, 0.1])
                    sphere(2, $fn=6);
                }
            }
        }
    }
}

// White fat lines in flesh
module fat_lines() {
    color(color_fat) {
        for(i = [-15:8:15]) {
            translate([i, 0, 5])
            rotate([0, 90, 0])
            scale([0.3, 1, 0.1])
            cylinder(h=30, r=0.5, $fn=8);
        }
    }
}

// Main assembly
color(color_flesh) salmon_fillet();
color(color_fat) fat_layer();
color(color_skin) skin_layer();
flakes();
scales();
fat_lines();

// Realistic 3D-printable Tuna Steak model
// Medium complexity with characteristic fish features

// Colors
$fn = 32;

// Main tuna steak body
module tuna_steak() {
    difference() {
        // Main oval steak shape
        scale([1.2, 1, 0.6])
            sphere(d=40);

        // Cut flat bottom for printing
        translate([0, 0, -20])
            cube([100, 100, 40], center=true);
    }
}

// Fish skin texture pattern
module skin_texture() {
    for(i = [0:10]) {
        rotate([0, 0, i*36])
            translate([0, 18, 0])
                scale([1, 0.1, 0.05])
                    sphere(d=8);
    }
}

// Fat layer pattern
module fat_layers() {
    color("white")
    for(z = [-8:-2:2]) {
        translate([0, 0, z])
            scale([1.2, 0.95, 0.05])
                sphere(d=40);
    }
}

// Muscle fiber texture
module fibers() {
    color("firebrick")
    for(angle = [0:30:360]) {
        rotate([0, 0, angle])
            for(r = [0:2:18]) {
                translate([r, 0, 0])
                    rotate([90, 0, 90])
                        scale([0.3, 0.3, 10])
                            cylinder(h=0.5, d=1);
            }
    }
}

// Bloodline pattern
module bloodline() {
    color("darkred")
    translate([0, -8, -2])
        scale([1.3, 0.2, 0.3])
            sphere(d=30);
}

// Combine all components
color("firebrick")
union() {
    // Main steak body
    tuna_steak();

    // Skin on top surface
    translate([0, 0, 11])
        skin_texture();

    // Internal fat layers
