// Realistic Shrimp Food Model for 3D Printing
// Medium complexity with appropriate details and textures

// Main parameters
shrimp_length = 80;
shrimp_height = 20;
body_segments = 8;

// Color definitions
shrimp_color = "#FF6B47";
darker_shrimp = "#E55A3D";

// Create the entire shrimp
module shrimp() {
    color(shrimp_color) {
        union() {
            // Head and upper body
            head();

            // Body segments
            body();

            // Tail fan
            tail();

            // Legs/swimmerets
            legs();

            // Antennae
            antennae();
        }
    }
}

// Shrimp head with eyes and rostrum
module head() {
    translate([shrimp_length * 0.85, 0, 0]) {
        rotate([0, 15, 0]) {
            // Main head shape
            scale([1.2, 1, 0.8]) 
                sphere(d=shrimp_height * 1.5, $fn=20);

            // Rostrum (pointy nose)
            translate([shrimp_height * 0.4, 0, shrimp_height * 0.3])
                rotate([0, -30, 0])
                    scale([2, 0.3, 0.3])
                        cylinder(h=shrimp_height, d=shrimp_height/3, $fn=8);

            // Eyes
            translate([shrimp_height * 0.5, shrimp_height * 0.4, shrimp_height * 0.5])
                sphere(d=shrimp_height/4, $fn=12);
            translate([shrimp_height * 0.5, -shrimp_height * 0.4, shrimp_height * 0.5])
                sphere(d=shrimp_height/4, $fn=12);
        }
    }
}

// Segmented body
module body() {
    for(i = [1:body_segments]) {
        segment = body_segments - i;
        pos = shrimp_length * (0.85 - (i * 0.08));
        scale_factor = 1 - (i * 0.08);

        translate([pos, 0, 0]) {
            rotate([0, -10, 0]) {
                scale([1, scale_factor, scale_factor * 0.8]) {
                    sphere(d=shrimp_height * 1.2, $fn=16);

                    // Add segment ridges
                    difference() {
                        sphere(d=shrimp_height * 1.2, $fn=16);
                        translate([0, 0, -shrimp_height/2])
                            cube([shrimp_height * 2, shrimp_height * 2, shrimp_height], center=true);
                    }
                }
            }
        }
    }
}

// Tail fan
module tail() {
    translate([0, 0, 0]) {
        rotate([0, -20, 0]) {
            // Tail segments
            for(i = [0:2]) {
                translate([-i * shrimp_height/4, 0, 0]) {
                    scale([0.8 - i*0.2, 1 - i*0.2, 0.6 - i*0.1]) {
                        sphere(d=shrimp_height, $fn=12);
                    }
                }
            }

            // Tail fan segments
            for(angle = [-45, -25, 0, 25, 45]) {
                rotate([0, 0, angle]) {
                    translate([-shrimp_height/2, 0, 0]) {
                        scale([1.5, 0.3, 0.8]) {
                            rotate([0, 30, 0])
                                cylinder(h=shrimp_height/2, d=shrimp_height/3, $fn=8);
                        }
                    }
                }
            }
        }
    }
}

// Legs/swimmerets
module legs() {
    for(pos = [0.6, 0.45, 0.3, 0.15]) {
        x_pos = shrimp_length * pos;

        // Left side legs
        for(side = [-1, 1]) {
            translate([x_pos, side * shrimp_height/3, -shrimp_height/4]) {
                rotate([0, 0, side * 45]) {
                    rotate([0, 45, 0]) {
                        scale([0.8, 0.3, 0.3]) {
                            cylinder(h=shrimp_height/2, d=shrimp_height/4, $fn=8);
                        }
                    }
                }
            }

            // Secondary swimmerets
            translate([x_pos - shrimp_height/4, side * shrimp_height/2, -shrimp_height/3]) {
                rotate([0, 0, side * 60]) {
                    rotate([30, 0, 0]) {
                        scale([0.6, 0.2, 0.2]) {
                            cylinder(h=shrimp_height/3, d=shrimp_height/5, $fn=6);
                        }
                    }
                }
            }
        }
    }
}

// Antennae
module antennae() {
    translate([shrimp_length * 0.85, 0, shrimp_height * 0.5]) {
        for(side = [-1, 1]) {
            translate([0, side * shrimp_height/3, 0]) {
                rotate([0, -30, side * 15]) {
                    scale([0.1, 0.1, 1]) {
                        cylinder(h=shrimp_height * 2, d=shrimp_height/8, $fn=6);
                    }
                }

                // Secondary antenna
                translate([0, side * shrimp_height/2, -shrimp_height/4]) {
                    rotate([0, -45, side * 20]) {
                        scale([0.08, 0.08, 0.7]) {
                            cylinder(h=shrimp_height * 1.5, d=shrimp_height/10, $fn=5);
                        }
                    }
                }
            }
        }
    }
}

// Surface texture details
module texture() {
    color(darker_shrimp) {
        // Shell pattern lines
        for(i = [1:body_segments]) {
            segment = body_segments - i;
            pos = shrimp_length * (0.85 - (i * 0.08));

            translate([pos, 0, shrimp_height/2]) {
                rotate([90, 0, 0]) {
                    cylinder(h=shrimp_height * 1.2, d=shrimp_height/10, $fn=20);
                }
            }
        }
    }
}

// Render the complete shrimp
union() {
    shrimp();
    texture();
}

// Add a flat base for better printing
translate([shrimp_length/2, 0, -shrimp_height/2 - 1])
    cube([shrimp_length, shrimp_height * 2, 2], center=true);

