// Mantle (main body) with realistic tapered shape
module squid_mantle() {
    color(squid_body_color) {
        // Main mantle body - elliptical shape
        translate([0, 0, body_height/2])
            scale([1, 0.8, 1])
                sphere(d=body_width);

        // Extended mantle tail
        translate([0, 0, -body_height/4])
            scale([0.8, 0.6, 1.2])
                cylinder(h=body_height/1.5, d1=body_width*0.9, d2=body_width*0.3);

