    // Left fin
    translate([0, body_width/2, 0])
        rotate([0, 20, 0])
            scale([1, 0.5, 0.2])
                sphere(d=fin_width);

    // Right fin
    translate([0, -body_width/2, 0])
        rotate([0, -20, 0])
            scale([1, 0.5, 0.2])
                sphere(d=fin_width);
