// Mahi (Dorado) Fish Fillet - Realistic Food Model
// Medium complexity with scales, skin texture, and flesh layers

$fn = 50;

// Overall dimensions (mm)
fillet_length = 80;
fillet_width = 40;
fillet_height = 15;

// Main fillet body
module fillet_body() {
    // Create tapered fillet shape
    hull() {
        translate([-fillet_length/2, 0, 0])
            scale([1, 0.8, 0.6])
            sphere(d=fillet_width);
        translate([fillet_length/2-10, 0, 0])
            scale([0.3, 0.4, 0.3])
            sphere(d=fillet_width*0.8);
    }
}

// Fish skin with scales texture
module fish_skin() {
    difference() {
        // Base skin layer
        color([0.1, 0.3, 0.5, 0.9])  // Metallic blue-green
        fillet_body();

        // Scale indentations
        for (i = [-30:5:30]) {
            for (j = [-15:4:15]) {
                if (i*i + j*j < 900) {
                    translate([i, j, -0.2])
                        scale([1, 0.7, 0.3])
                        sphere(d=2.5);
                }
            }
        }
    }
}

// Flesh layer with muscle segments
module flesh_layer() {
    color([1, 0.75, 0.6])  // Cooked mahi color
    difference() {
        // Main flesh
        translate([0, 0, -0.5])
            scale([0.95, 0.95, 0.8])
            fillet_body();

        // Muscle segments
        for (x = [-25:8:25]) {
            translate([x, 0, -2])
                rotate([0, 0, 90])
                cube([0.5, fillet_width*2, fillet_height], center=true);
        }

        // Flake lines
        for (angle = [30:15:150]) {
            rotate([0, 0, angle])
                translate([-fillet_length/2, 0, -1])
                    cube([fillet_length, 0.3, fillet_height]);
        }
    }
}

// Grilling marks
module grill_marks() {
    color([0.6, 0.4, 0.25])  // Darker grilled areas
    for (i = [-20:10:20]) {
        translate([i, -fillet_width/2, 0.5])
            cube([2, fillet_width, 0.5]);
    }
}

// Lemon wedge garnish
module lemon_wedge() {
    color([1, 0.9, 0.2])
    translate([fillet_length/2+5, fillet_width/3, 5])
    rotate([0, 0, 15])
    difference() {
        // Main wedge
        cylinder(h=8, r1=8, r2=0);

        // Center cut
        translate([0, -4, 4])
            cube([8, 8, 8], center=true);

        // Pulp segments
        for (i = [0:60:300]) {
            rotate([0, 0, i])
                translate([2, 0, 0])
                    cube([0.5, 0.5, 8]);
        }
    }
}

// Assembly
union() {
    // Base fillet
    union() {
        fish_skin();
        flesh_layer();
    }

    // Grilling on top
    grill_marks();

    // Garnish
    lemon_wedge();
}

