    color("burlywood") {
        // Top fin
        difference() {
            translate([-fish_length/4, fish_width/2, 0]) 
                rotate([0, 0, 45]) 
                scale([1, 0.3, 0.1]) 
                cylinder(h=fish_width/2, d1=fish_width/4, d2=0, $fn=3);
            translate([-fish_length/4, fish_width/2, -1]) 
                cube([fish_width, fish_width/4, 2], center=true);
