// Main body module
module carp_food() {
    // Main fish-shaped body
    union() {
        // Torso - elongated ellipsoid
        scale([1, 0.6, 0.5])
            sphere(d=width, $fn=30);

        // Tail section - tapering end
        translate([length/2 - 10, 0, 0])
            scale([1.2, 0.3, 0.3])
                rotate([0, 90, 0])
                    cylinder(h=15, d1=width*0.8, d2=width*0.2, $fn=20);

        // Head - rounded front
        translate([-length/2 + 5, 0, 0])
            scale([0.8, 0.7, 0.7])
                sphere(d=width, $fn=25);

        // Dorsal fin ridge
        for(i = [-20:5:15]) {
            translate([i, 0, height/2 - 2])
                rotate([90, 0, 0])
                    cylinder(h=4, d1=6, d2=2, $fn=6);
        }

        // Side fins
        translate([-10, width/2 - 2, 0])
            rotate([0, 0, 45])
                scale([1, 0.3, 0.2])
                    cylinder(h=8, d=width/3, $fn=12);

        mirror([0, 1, 0])
            translate([-10, width/2 - 2, 0])
                rotate([0, 0, 45])
                    scale([1, 0.3, 0.2])
                        cylinder(h=8, d=width/3, $fn=12);

        // Eye sockets
        translate([-length/2 + 8, width/3, height/3])
            sphere(d=4, $fn=12);
        translate([-length/2 + 8, -width/3, height/3])
            sphere(d=4, $fn=12);
    }

    // Eye details
    difference() {
        // Eyes
        translate([-length/2 + 8, width/3, height/3])
            sphere(d=3.5, $fn=12);
        translate([-length/2 + 8, -width/3, height/3])
            sphere(d=3.5, $fn=12);

        // Pupils
        translate([-length/2 + 9, width/3, height/3 + 1])
            sphere(d=1.5, $fn=8);
        translate([-length/2 + 9, -width/3, height/3 + 1])
            sphere(d=1.5, $fn=8);
    }

    // Mouth opening
    difference() {
        // Head detail
        translate([-length/2 + 2, 0, -height/4])
            rotate([0, 0, 90])
                cylinder(h=8, d=6, $fn=20);

        // Mouth cavity
        translate([-length/2 + 1, 0, -height/4])
            rotate([0, 0, 90])
                cylinder(h=10, d=4, $fn=20);
    }

    // Scale texture pattern
    for(x = [-20:8:20]) {
        for(y = [-width/3:6:width/3]) {
            if(norm([x, y]) < width/2 - 3) {
                translate([x, y, height/2 - 0.5])
                    scale([1.2, 0.8, 0.2])
                        sphere(d=2, $fn=6);
            }
        }
    }

    // Gill details
    for(i = [0:10:30]) {
        translate([-length/4 + i/3, width/2 - 1, 0])
            rotate([0, 0, -30])
                scale([0.5, 0.1, 0.5])
                    cylinder(h=3, d=2, $fn=8);
        translate([-length/4 + i/3, -width/2 + 1, 0])
            rotate([0, 0, 30])
                scale([0.5, 0.1, 0.5])
                    cylinder(h=3, d=2, $fn=8);
    }

    // Bottom stabilizing ridge (prevents rolling)
    translate([0, 0, -height/2 + 1])
        cube([length - 10, width/3, 2], center=true);
}

